function.textSource:
      function AfterPForm return boolean is

l_niv entite_structure.niveau%type;
t_recup taux_recup.taux%TYPE;
t_recup_max taux_recup.taux%TYPE;
DATDEBEX varchar(4);

begin
	
  SELECT es.niveau into l_niv
  FROM entite_structure es
  WHERE es.codcamo = :p_param6;

  -- // Pour Récupérer le taux de TVA
 
 		select min(taux) into t_recup 
 		from taux_recup tr, centre_activite c, centre_activite c2
		where 
		c2.codcamo=:p_param6 and ((c2.codcamo = c.codcamo) or (c.caniv1 = c2.codcamo) or (c.caniv2 = c2.codcamo) or (c.caniv3 = c2.codcamo)
		or (c.caniv4 = c2.codcamo)) and c.filcode = tr.filcode and  to_char(tr.annee) =:P_PARAM7;

		select tr3.TAUX into t_recup_max
		from taux_recup tr3
		where tr3.ANNEE IN (SELECT MAX(tr2.ANNEE) from taux_recup tr2);

		if (t_recup!=0 or t_recup!=null or t_recup!='0') then 
				:P_TAUX := t_recup;
				--srw.message(1, 'P_TAUX = ' ||  :P_TAUX);
		else
				:P_TAUX := t_recup_max;  -- La Dernière valeur du Taux de TVA ( table Taux_recup )
				--srw.message(1, 'P_TAUX MAX = ' ||  :P_TAUX);
		end if;

  -- // ---------------------------------------------
	
	select to_char(dbex.datdebex,'YYYY') into DATDEBEX from datdebex dbex; -- Récupérer l'année fonctionelle
  
  --srw.message(1, 'P_Param7 = ' ||  :P_PARAM7);
  --srw.message(1, 'DATDEBEX = ' ||  DATDEBEX);
  
	IF (:P_PARAM7 <= DATDEBEX) THEN  -- Si l'exercice demandé est égal ou antérieur à l'année fonctionnelle en cours
  
  :P_T_WHERE:= ' AND tr.annee = lig.annee AND to_char(lig.annee) = :P_PARAM7 '; 
  
  --srw.message(1, 'P_T_WHERE = ' ||  :P_T_WHERE);
    
  CASE l_niv
  
       WHEN 0 Then :p_where:= ' and  ca.codcamo = :p_param6 '; :P_T_WHERE:=:P_T_WHERE; 
       WHEN 1 Then :p_where:= ' and  ca.caniv1  = :p_param6 '; :P_T_WHERE:=:P_T_WHERE;
       WHEN 2 Then :p_where:= ' and  ca.caniv2  = :p_param6 '; :P_T_WHERE:=:P_T_WHERE;
       WHEN 3 Then :p_where:= ' and  ca.caniv3  = :p_param6 '; :P_T_WHERE:=:P_T_WHERE;
       WHEN 4 Then :p_where:= ' and  ca.caniv4  = :p_param6 '; :P_T_WHERE:=:P_T_WHERE;
       WHEN 5 Then :p_where:= ' and  ca.caniv5  = :p_param6 '; :P_T_WHERE:=:P_T_WHERE;
       WHEN 6 Then :p_where:= ' and  ca.caniv6  = :p_param6 '; :P_T_WHERE:=:P_T_WHERE;
       
	END CASE ;
	
	ELSE -- L'exercice demandé est ultérieure à l'année fonctionnelle en cours
	
	--srw.message(1, 'L''exercice demandé : ' || :P_PARAM7 || ' est ultérieure à l''année fonctionnelle en cours DATDEBEX : ' ||  DATDEBEX);
	
	:P_ANNEE  := ' (SELECT MAX(tr2.ANNEE) from taux_recup tr2) ';
	:P_WHERE := ' and tr.annee in ' || :P_ANNEE || ' AND to_char(lig.annee) = ' || :P_PARAM7 ;
	
	--srw.message(1, 'P_WHERE = ' ||  :P_WHERE);
	--srw.message(1, 'P_T_WHERE = ' ||  :P_T_WHERE);
	
	CASE l_niv
  
       WHEN 0 Then :P_WHERE:=:P_WHERE || ' and  ca.codcamo = :p_param6 ';
       WHEN 1 Then :P_WHERE:=:P_WHERE || ' and  ca.caniv1  = :p_param6 ';
       WHEN 2 Then :P_WHERE:=:P_WHERE || ' and  ca.caniv2  = :p_param6 ';
       WHEN 3 Then :P_WHERE:=:P_WHERE || ' and  ca.caniv3  = :p_param6 ';
       WHEN 4 Then :P_WHERE:=:P_WHERE || ' and  ca.caniv4  = :p_param6 ';
       WHEN 5 Then :P_WHERE:=:P_WHERE || ' and  ca.caniv5  = :p_param6 ';
       WHEN 6 Then :P_WHERE:=:P_WHERE || ' and  ca.caniv6  = :p_param6 ';
       
	END CASE ;
		
	END IF;
   
 -- // --------------------------------------------- 
  
  	
  return (true);
  end;

      function R_POSTEFormatTrigger return boolean is
reste number;
begin
reste := mod(:CS_1,2);
if reste=0 then
-- manip pour alterner les couleurs
    srw.attr.mask := SRW.BFCOLOR_ATTR;
 srw.attr.bfcolor :='white';
 srw.set_attr (0,srw.attr);
end if;
  return (TRUE);
end;

      function CF_ecartFormula return Number is
ecart number;
begin
ecart := :reestime - :budget ;
return (ecart);  
end;

      function CF_avancmtFormula return Number is
av number;
begin
	if :budget=0 then
		av := 0;
		else
av := (:realise * 100)/ :budget ;
end if;
return (av);  
end;

      function R_listCAFormatTrigger return boolean is
l_niv entite_structure.niveau%type;

begin
	
  SELECT es.niveau into l_niv
  FROM entite_structure es
  WHERE es.codcamo = :p_param6;
  
  if :P_PARAM8 <> 'O' and l_niv <> 0 then 
    return (FALSE);
  else return(TRUE);
  	end if;
end;

      function M_cumulFormatTrigger return boolean is
l_niv entite_structure.niveau%type;

begin
	
  SELECT es.niveau into l_niv
  FROM entite_structure es
  WHERE es.codcamo = :p_param6;
  
  if :P_PARAM8 <> 'O' and l_niv <> 0 then 
    return (TRUE);
  else return(FALSE);
  	end if;
end;

      function CF_cumul_avancFormula return Number is
av number;
begin
	if :budget1=0 then
		av := 0;
		else
av := (:realise1 * 100)/ :budget1 ;
end if;
return (av);  
end;

      function CF_cumul_ecartFormula return Number is
ecart number;
begin
ecart := :reestime1 - :budget1 ;
return (ecart);  
end;

      function F_ca_non1FormatTrigger return boolean is
l_niv entite_structure.niveau%type;
begin
	
  SELECT es.niveau into l_niv
  FROM entite_structure es
  WHERE es.codcamo = :p_param6;
  
  
  if l_niv = 0 then 
    return (FALSE);
  else return(TRUE);
  	end if;
end;

      function B_ca_non1FormatTrigger return boolean is
l_niv entite_structure.niveau%type;
begin
	
  SELECT es.niveau into l_niv
  FROM entite_structure es
  WHERE es.codcamo = :p_param6;
  
  
  if l_niv = 0 then 
    return (FALSE);
  else return(TRUE);
  	end if;
end;

      function M_1FormatTrigger return boolean is
l_niv entite_structure.niveau%type;

begin
	
  SELECT es.niveau into l_niv
  FROM entite_structure es
  WHERE es.codcamo = :p_param6;
  
  if :P_PARAM8 = 'O' and l_niv <> 0 then 
    return (TRUE);
  else return(FALSE);
  	end if;
end;

      function CF_avan_totalFormula return Number is
av number;
begin
	if :CS_budget=0 then
		av := 0;
		else
av := (:CS_realise * 100)/ :CS_budget ;
end if;
return (av);  
end;

      function CF_avan_globalFormula return Number is
av number;
begin
	if :CS_cumul_budg=0 then
		av := 0;
		else
av := (:CS_cumul_real * 100)/ :CS_cumul_budg ;
end if;
return (av);  
end;

      function CF_taux_recupFormula return Number is

-- Declaration des variables
t_recup taux_recup.taux%TYPE;
t_recup_max taux_recup.taux%TYPE;

begin
	
select min(taux) into t_recup
from taux_recup tr,
centre_activite c,
centre_activite c2
where 
c2.codcamo=:p_param6
and ((c2.codcamo = c.codcamo)
or (c.caniv1 = c2.codcamo)
or (c.caniv2 = c2.codcamo)
or (c.caniv3 = c2.codcamo)
or (c.caniv4 = c2.codcamo)) 
and c.filcode = tr.filcode
and  to_char(tr.annee) =:P_PARAM7;

select tr3.TAUX into t_recup_max
from taux_recup tr3
where tr3.ANNEE IN (SELECT MAX(tr2.ANNEE) 
from taux_recup tr2);

if (t_recup!=0 or t_recup!=null or t_recup!='0') then
		return t_recup;
else
		return t_recup_max;
end if;

-- Exception
exception
when no_data_found then 
return t_recup_max;

end;
