function.textSource:
      function CF_m1Formula return Number is
begin
  IF ((:prodm1 is not null) OR (:absm1 IS NOT NULL)) then
  	return (nvl(:prodm1,0) + nvl(:absm1,0));
  else
  	return '';
  end if;
end;

      function CF_1Formula return Number is
begin
  return ((:prodm2 * :cj1) / :cj2);
end;

      function CF_2Formula return Number is
begin
  return((:prodm1 * :cjours) / :cj1);
end;

      function CF_3Formula return Number is
begin
  return ((:CF_m1 * :cjours) / :cj1);
end;

      function CF_4Formula return Number is
begin
  IF ((:prod is not null) OR (:CF_2 is not null)) then
  	return (nvl(:prod,0) - nvl(:CF_2,0));
  ELSE
	return '';
  END IF;
end;

      function CF_5Formula return Number is
begin
  IF ((:abscence is not null) OR (:absm1 is not null)) then
  	return (nvl(:abscence,0) - nvl(:absm1,0));
  ELSE
	return '';
  END IF;
end;

      function CF_6Formula return Number is
begin
  IF ((:CF_m is not null) OR (:CF_3 is not null)) then
  	return (nvl(:CF_m,0) - nvl(:CF_3,0));
  ELSE
	return '';
  END IF;
end;

      function CF_7Formula return Number is
begin
 IF ((:prod = 0) OR (:prod is null)) then
	return (null);
 else
  return (:CF_4 / :prod *100);
 end if;
end;

      function CF_8Formula return Number is
begin
 IF ((:abscence = 0) OR (:abscence is null)) then 
	return(null);
 else
  return (:CF_5 /:abscence *100);
 end if;
end;

      function CF_9Formula return Number is
begin
  IF (:CF_3 = 0 OR :CF_3 is null) then
	return (null);
  else
       return (:CF_6 /:CF_3 *100);
  end if;
end;

      function CF_10Formula return Number is
begin
	if (:CS_totcalm = 0 OR :CS_totcalm is null) then 
		return (null);
	else
  		return (:CS_vol/:CS_totcalm*100);
	end if;
end;

