      function AfterPForm return boolean is
begin
	 :P_WHERE := '';
	 IF :P_PARAM6 IS NOT NULL THEN
	   :P_WHERE := 'and sti.coddir = TO_NUMBER(:P_PARAM6)';
	 END IF;

   :P_PERIME:=PACK_GLOBAL.LIRE_PERIME(:P_GLOBAL);
