:p_where := 'from stock_immo_sc s, ligne_bip lb, (select s1.pid,s1.ident, s1.cafi, sum(NVL(s1.consojh,0)) consojh_N, sum(NVL(s1.consoft,0)) consoft_N from stock_immo_sc s1 group by pid,ident,cafi) a, '||:p_from2||' liste_fi, '||:p_from||' liste_ca where s.ident=a.ident(+) and s.pid = a.pid(+) and s.cafi = a.cafi(+) and s.cafi = liste_fi.CODCAMO and s.cada = liste_ca.CODCAMO and s.PID = lb.PID(+) and s.CODCAMO = lb.CODCAMO(+) and s.ICPI = lb.ICPI(+) and s.DPCODE = lb.DPCODE(+) ';
end if;
--srw.message(1,'l_doss_proj : '||l_doss_proj); --srw.message(1,'l_projet : '||l_projet);
if(l_doss_proj <> 'VIDE') then :p_where := :p_where||l_doss_proj; end if;
if(l_projet <> 'VIDE') then :p_where := :p_where||l_projet; end if;
--srw.message(1,NVL(:p_where,'vide'));
END IF;
return (TRUE); end;

      function G_pidGroupFilter return boolean is
begin
  if (length(:p_from)<30000)then
    return (TRUE);
  else           
  	 return (FALSe);
  end if;
end;

      function G_erreurGroupFilter return boolean is
begin
  if (length(:p_from)>=30000)then
    return (TRUE);
  else           
  	 return (FALSe);
  end if;
end;
