function.textSource:
      function CF_AN_MOINS1Formula return Number is
	l_res NUMBER(4);
begin
  	l_res := TO_NUMBER(:dc_ancouran) - 1;
	RETURN(l_res);
end;

      function CF_AN_MOINS2Formula return Number is
	l_res NUMBER(4);
begin
  	l_res := TO_NUMBER(:dc_ancouran) - 2;
	RETURN(l_res);
end;

      function CF_AN_MOINS3Formula return Number is
	l_res NUMBER(4);
begin
  	l_res := TO_NUMBER(:dc_ancouran) - 3;
	RETURN(l_res);
end;

      function CF_AN_MOINS4Formula return Number is
	l_res NUMBER(4);
begin
  	l_res := TO_NUMBER(:dc_ancouran) - 4;
	RETURN(l_res);
end;

      function R_AMTLIS01FormatTrigger return boolean is
begin
IF ( (NVL(:dc_mt_an_cour,0)!=0)
	OR (NVL(:dc_mt_an_moins1,0)!=0)
	OR (NVL(:dc_mt_an_moins2,0)!=0)
	OR (NVL(:dc_mt_an_moins3,0)!=0)
	OR (NVL(:dc_mt_an_moins4,0)!=0) )
THEN
	RETURN TRUE;
ELSE
  	RETURN FALSE;
END IF;

end;
