      function AfterPForm return boolean is
 l_filsigle filiale_cli.filsigle%TYPE;
begin
 

 if :P_PARAM6 is not NULL then 
	:p_where := 'and c.soccont = ''' || :P_PARAM6 ||''' ';
  end if;

  if :P_PARAM7 is not NULL then 
	:p_where := :p_where || ' and c.numcont = ''' || :P_PARAM7 ||'''';
  end if;
  
  if :P_PARAM8 is not NULL then 
	:p_where := :p_where || ' and c.cav = lpad(''' || :P_PARAM8 ||''',3,''0'')';
  end if;
  if  :P_PARAM3 is not NULL then 
	:p_where := :p_where || ' and c.filcode = ''' || :P_PARAM3 ||''' ';
  end if;
if :P_PARAM5 !='0' then 
	:p_where := :p_where || ' and to_char(c.ccentrefrais) = ''' || :P_PARAM5 ||''' ';
 end if;
 --srw.message(1,:p_where);
  return (TRUE);
 
end;

