dataSource.select:
SELECT codsg, soccode1, cpnom, dureepresence, numcontav FROM ( SELECT sr.codsg, sr.soccode soccode1, sr.cpident || ' - ' || r1.rnom cpnom, DECODE(sr.soccode,'SG..','',Pack_Util_Situ.DureePresenceRessourceMois(sr.ident, SYSDATE) || ' mois') dureepresence, DECODE(sr.soccode,'SG..','', trim(lc.numcont) || ' - ' || decode(c.top30,'N',substr(lc.cav,2,2),'O',decode(lc.cav,'000',null,lc.cav))) numcontav
FROM SITU_RESS sr, RESSOURCE r1,LIGNE_CONT lc, contrat c WHERE sr.ident = :P_PARAM6 and c.cav = lc.cav and c.numcont = lc.numcont and c.soccont = lc.soccont AND (sr.ident, TO_CHAR(sr.datsitu,'dd/mm/yyyy')) IN (SELECT ident, TO_CHAR(MAX(datsitu),'dd/mm/yyyy') FROM SITU_RESS WHERE ident = :P_PARAM6 GROUP BY ident ) AND r1.ident=sr.cpident AND lc.lresfin IN (SELECT MAX(l.lresfin) FROM LIGNE_CONT l WHERE l.ident=sr.ident AND l.lresdeb < SYSDATE) AND lc.ident(+) =sr.ident
UNION SELECT sr.codsg, sr.soccode soccode1, sr.cpident || ' - ' || r1.rnom cpnom, DECODE(sr.soccode,'SG..','',Pack_Util_Situ.DureePresenceRessourceMois(sr.ident, SYSDATE) || ' mois') dureepresence, '' numcontav FROM SITU_RESS sr, RESSOURCE r1 WHERE sr.ident = :P_PARAM6 AND (sr.ident, TO_CHAR(sr.datsitu,'dd/mm/yyyy')) IN (SELECT ident, TO_CHAR(MAX(datsitu),'dd/mm/yyyy') FROM SITU_RESS WHERE ident = :P_PARAM6 GROUP BY ident ) AND r1.ident=sr.cpident ) WHERE ROWNUM<2 ;

function.textSource:
      function B_31FormatTrigger return boolean is
begin
  
   if(:soccode2!='SG..')then
    return (TRUE);
  else
  	 return (FALSE);
  end if; 
  
end;

      function B_32FormatTrigger return boolean is
begin
  
   if((:soccode2!='SG..')AND (:rtype = 'P'))then
    return (TRUE);
