function.textSource:
      function AfterPForm return boolean is


begin
  
  if :p_param7 = '---'
  	then
  	:p_param7 := null; 
  end if;
  
  :P_NUMSEQ := pack_cout_standard_ke.INSERT_TMP_COPI_SYNTHESE(null,:P_PARAM6 , :P_PARAM7,1); 
  
  return (TRUE);
end;

      function R_G_CODBRFormatTrigger return boolean is
begin
 IF (:CS_compteur mod 2 = 0)
   THEN
      srw.set_background_fill_color('r88g75b88');
 else
 	 srw.set_background_fill_color('white');
       END IF;

  return (TRUE);
end;

      function CF_REAL_ARBFormula return Number is
begin
	if (:EU_ARBDECIDES <> 0)
	then
  	return((round(:EU_REALISES)/round(:EU_ARBDECIDES))*100);
	else
		return 0;
	end if;
end;

      function CF_REAL_ARB_CANTFormula return Number is
begin
	if ((:EU_ARBDECIDES+:EU_CANTDECIDES) <> 0)
	then
  	return((round(:EU_REALISES)/(round(:EU_ARBDECIDES)+round(:EU_CANTDECIDES)))*100);
	else
		return 0;
	end if;
end;

      function CF_SUMREAL_SUMARBFormula return Number is
begin
	if (:SumEU_ARBDECIDESPerANNEE <> 0)
	then
  	return((round(:SumEU_REALISESPerANNEE)/round(:SumEU_ARBDECIDESPerANNEE))*100);
	else
		return 0;
	end if;
end;

      function CF_SUMREAL_SUMARB_SUMCANTFormu return Number is
begin
	if ((:SumEU_ARBDECIDESPerANNEE+:SumEU_CANTDECIDESPerANNEE) <> 0)
	then
  	return((round(:SumEU_REALISESPerANNEE)/(round(:SumEU_ARBDECIDESPerANNEE)+round(:SumEU_CANTDECIDESPerANNEE)))*100);
	else
		return 0;
	end if;
end;

      function AfterReport return boolean is
begin
  
  
  DELETE FROM TMP_COPI_SYNTHESE_ETATS WHERE NUMSEQ = :P_NUMSEQ ; 
  
  COMMIT ; 
  
  return (TRUE);
end;
