function.textSource:
      function AfterPForm return boolean is
 l_param   VARCHAR2(40);
 l_liste   VARCHAR2(1024);
begin
 if :P_PARAM6='Toute' then
l_liste :=pack_global.lire_globaldata(:P_GLOBAL).chefprojet;
			
:p_where := :p_where || ' and  a.ident in (
		select distinct r.ident
		from situ_ress_full f, ressource r, datdebex d
		where f.ident=r.ident
		and (TRUNC(f.datsitu,''YEAR'') <=TRUNC(d.datdebex,''YEAR'') or f.datsitu is null)
		and (TRUNC(f.datdep,''YEAR'')>=TRUNC(d.datdebex,''YEAR'') or f.datdep is null)
    and f.type_situ=''N''
		and ((INSTR('''||l_liste||''',TO_CHAR(f.cpident,''FM00000''))>0)
		  or(INSTR('''||l_liste||''',TO_CHAR(f.ident,''FM00000''))>0)))';

 else

 :p_where := :p_where || ' and  a.ident=:P_PARAM6 ';

 end if;


   --srw.message(1,:P_where);  

  return (TRUE);
end;

      function CF_total_ligneFormula return Number is
total number(8,2);
begin
  total := NVL(:MOIS_1,0)+NVL(:MOIS_2,0)+ NVL(:MOIS_3,0) +
           NVL(:MOIS_4,0) + NVL(:MOIS_5,0) + NVL(:MOIS_6 ,0)+
           NVL(:MOIS_7,0) + NVL(:MOIS_8,0) + NVL(:MOIS_9,0) +
           NVL(:MOIS_10,0) + NVL(:MOIS_11,0) + NVL(:MOIS_12,0);

return total;
end;

      function CF_TOTAl_JOUR_OUVREFormula return Number is
l_total NUMBER;
begin
  l_total := :NBJOUR_1 + :NBJOUR_2 + :NBJOUR_3 + :NBJOUR_4 +
	     :NBJOUR_5 + :NBJOUR_6 + :NBJOUR_7 + :NBJOUR_8 +
	     :NBJOUR_9 + :NBJOUR_10 + :NBJOUR_11 + :NBJOUR_12 ;
 return l_total;
end;
