      function AfterPForm return boolean is
begin
	if substr(LPAD(:p_param2, 7, '0'),4,2) = '00' then
		:p_pole := substr(LPAD(:p_param2, 7, '0'),1,3) || '__';
	else 
		:p_pole := substr(LPAD(:p_param2, 7, '0'),1,5);
	end if;
  if substr(LPAD(:p_param2, 7, '0'),1,5) = '00000' then
    :p_pole := '_____';
  end if;

	--srw.message(1,:p_pole);

	delete from tmpedsstr;
	pack_verif_edsstr.f_ins_edsstr4(:p_pole);
  return (TRUE);
end;

      FUNCTION f_datsitu_recente(p_ident in number) RETURN date IS
datsit date;
BEGIN
  select max(datsitu) into datsit from situ_ress
where ident=p_ident;
return(datsit);
END;

      function CF_tot_poleFormula return Number is
begin
  RETURN(:cs_tot_sg+:cs_tot_ssii);
end;

      function AfterReport return boolean is
begin
  delete from tmpedsstr;
  return (TRUE);
end;
