function.textSource:
      function AfterPForm return boolean is
begin
	
  
  :P_WHERE :='';
  
	IF :P_PARAM5!= 0 THEN 
		:P_WHERE := ' and fac.fcentrefrais= ''' || :P_PARAM5 ||'''';
  END IF;
	
	IF (:P_PARAM10 IS NOT NULL) THEN
		:P_WHERE :=  :P_WHERE || ' and fac.num_expense = ''' || :P_PARAM10 || '''';
	ELSE
		:P_WHERE :=  :P_WHERE || ' and fac.socfact = RPAD( ''' || :P_PARAM6 || ''' ,4) and fac.numfact = RPAD( ''' || :P_PARAM7 || ''', 15) and fac.typfact = '''|| :P_PARAM8 || 
								  ''' and fac.datfact = TO_DATE( ''' || :P_PARAM9 || ''', ''DD/MM/YYYY'') ';
	END IF;  
  
  --srw.message(1,:P_WHERE);  
  
  return (TRUE);
  
end;

      function CF_LIBFILIALEFormula return VARCHAR2 is
  l_libfiliale VARCHAR2(50);
begin
  l_libfiliale := RTRIM(pack_utile.f_get_filiale(RTRIM(:P_PARAM3)));
  RETURN(l_libfiliale);
end;

      function CF_CFRAISFormatTrigger return boolean is
Text varchar2(60);
begin
  if NVL(LENGTH(:P_PARAM5), 0)!= 0 then 
	 Select LIBCFRAIS into Text from centre_frais where to_char(codcfrais) = :P_PARAM5;
	 Text := :P_PARAM5 || ' - ' ||Text;
	 SRW.SET_FIELD_CHAR(0,Text);
  end if;
  return (TRUE);
end;

      function fmontant_ht return Number is
begin
  if (:dc_typfact = 'A') THEN
  	return 0-:dc_fmontht;
  ELSE 
  	return :dc_fmontht;
  	END IF;
end;

      function fmontant_ttc return Number is
begin
  if (:dc_typfact = 'A') THEN
  	return 0-:dc_fmontttc;
  ELSE 
  	return :dc_fmontttc;
  	END IF;
end;

      function lmontant_ht return Number is
begin
  if (:dc_typfact = 'A') THEN
  	return 0-:dc_lmontht;
  ELSE 
  	return :dc_lmontht;
  	END IF;
end;
