function.textSource:
      -- ----------------------------------------------------------------------------
-- Calcul de Moisconso et Anneeconso qui servent au calcul de 
--       - consomme dec(N-1)
--       - consomme annee N
--
-- Modif QHL 10/8/2000 : pour trouver le dernier mensuel on utilise moismens au lieu
--                       du calcul (cmensuelle-2)...
--            
-- ---------------------------------------------------------------------------
function AfterPForm return boolean is
begin
-- Beg Modif QHL 10/8/2000
	select to_number(to_char(moismens, 'mm'))   into :p_MoisConso from datdebex;
	select to_number(to_char(moismens, 'yyyy')) into :p_AnConso   from datdebex;
-- End Modif QHL 10/8/2000

	-- des date en dure font parties du spec
 	:P_DecAnNM1 := '01/12/' || to_char(:p_AnConso - 1);  
 	:P_NovAnN   := '30/11/' || to_char(:p_AnConso);
   
	:p_nom_etat := 'FACINTG1';
	:p_amort := 'NON AMORTISSABLES';
  
   -- --srw.message(1,:P_DecAnNM1);
   -- --srw.message(1,:P_NovAnN);
  return (TRUE);
end;

      function R_facintgm1FormatTrigger return boolean is
begin
	 if :dc_dec_1_I+:dc_conso_mois_I+:dc_conso_an_I+:dc_facint_I = 0
  	then return (FALSE);
  end if;
	return (TRUE);

end;

      function R_mo1FormatTrigger return boolean is
begin
	if :CS_dept_dec_1_I+:CS_dept_conso_mois_I+:CS_dept_conso_an_I+:CS_dept_facint_mois_I = 0
  	then return (FALSE);
  end if;
  return (TRUE);
end;

      function R_dept1FormatTrigger return boolean is
begin
  
  	return (TRUE);

end;

      function R_ca1FormatTrigger return boolean is
begin
 
	return (true);


end;

      function G_pidGroupFilter return boolean is
begin
  
  
  return (FALSE);
end;

      function R_MO_IIFormatTrigger return boolean is
begin
  if :CS_dept_dec_1_II+:CS_dept_conso_mois_II+:CS_dept_conso_an_II+:CS_dept_facint_mois_II = 0
  	then return (FALSE);
  end if;
  
  return (TRUE);
end;

      function G_moGroupFilter return boolean is
begin
  
  return (TRUE);
end;

      function R_FACINTGM_IIFormatTrigger return boolean is
begin
   if :dc_dec_1_II+:dc_conso_mois_II+:dc_conso_an_II+:dc_facint_II = 0
  	then return (FALSE);
   end if;
   
  return (TRUE);
end;

      function CF_RECTIFFormula return Number is
	l_sum number(8,2);
begin
  --l_sum := :dc_factint_total	- :dc_conso_mois_total;
  l_sum := :CF_rectif_I	+ :CF_rectif_II;
	return(l_sum);
end;

      function CF_1Formula return Number is
l_sum number(8,2);
begin
  l_sum := :dc_dec_1_total + :dc_conso_an_total;
	return (l_sum);  
end;

      function CF_total_fi_IIFormula return Number is
l_sum number(8,2);
begin
  l_sum := :dc_dec_1_II + :dc_conso_an_II;
	return (l_sum);  
end;

      function CF_total_fi_IFormula return Number is
l_sum number(8,2);
begin
  l_sum := :dc_dec_1_I + :dc_conso_an_I;
	return (l_sum);  
end;

      function CF_rectif_IIFormula return Number is
	l_sum number(8,2);
begin
  l_sum := :dc_facint_II	- :dc_conso_mois_II;
	return(l_sum);  
end;

      function CF_rectif_IFormula return Number is
	l_sum number(8,2);
begin
  l_sum := :dc_facint_I	- :dc_conso_mois_I;
	return(l_sum);  
end;
