      ------------------------------------------------------------------------------------------
-- Rôle : Construire un Where conditionnel. La chaine construite sera affectée au parametre
-- de P_WHERE. 
------------------------------------------------------------------------------------------
function AfterPForm return boolean is
begin
  :p_where := '';
  :p_where := :p_where || ' and l.ident = ''' || :P_PARAM6 ||'''';
  
    
  IF ((:P_PARAM7 IS NOT NULL) and (:P_PARAM8 IS NOT NULL)) THEN 
	:p_where := :P_where || ' and f.datfact >= TO_DATE(''' || :P_PARAM7 || ''',''dd/mm/yyyy'')';
        :p_where := :P_where || ' and f.datfact <= TO_DATE(''' || :P_PARAM8 || ''',''dd/mm/yyyy'')';
  END IF;

  IF ((:P_PARAM7 IS NULL) and (:P_PARAM8 IS NULL)) THEN 
        :p_where := :P_where || ' and f.datfact <= sysdate';
  END IF;

  IF ((:P_PARAM7 IS NOT NULL) and (:P_PARAM8 IS NULL)) THEN 
	:p_where := :P_where || ' and f.datfact >= TO_DATE(''' || :P_PARAM7 || ''',''dd/mm/yyyy'')';
        :p_where := :P_where || ' and f.datfact <= sysdate';
	:P_PARAM8 := to_char(sysdate,'dd/mm/yyyy');
  END IF;

-- -------------
  

IF (:P_PARAM5 !='0') THEN 
	:p_where := :p_where || ' and to_char(f.FCENTREFRAIS) = ''' || :P_PARAM5 ||''''; 
  END IF;

  :p_where := :p_where || ' ';
   --srw.message(1,:p_where);
  RETURN (TRUE);
END;

      FUNCTION RtrimEtoile(P_Text In VARCHAR2) RETURN VARCHAR2 IS
  meta VARCHAR2(1);
  L_Text VARCHAR2(7);
BEGIN
  L_Text := P_Text;
  meta := SUBSTR(P_Text, NVL(length(P_Text), 0),1);
  IF (meta = ' ') OR (meta = '*') THEN
      L_Text := RTRIM(P_Text,meta);   
  END IF;
  RETURN(L_Text);
END;

