function.textSource:
      -- ----------------------------------------------------------------------------
-- Calcul de Moisconso et Anneeconso qui servent au calcul de 
--       - consomme dec(N-1)
--       - consomme annee N
--
-- Modif QHL 10/8/2000 : pour trouver le dernier mensuel on utilise moismens au lieu
--                       du calcul (cmensuelle-2)...
--            
-- ---------------------------------------------------------------------------
function AfterPForm return boolean is
	l_global pack_global.GlobalData;
	l_doss_proj VARCHAR2(5000);
	chaine_doss_proj VARCHAR2(5000);
	l_projet VARCHAR2(5000);
	chaine_projet VARCHAR2(5000);
	l_application VARCHAR2(5000);
	chaine_application VARCHAR2(5000);
	l_ca_fi VARCHAR2(5000); 
	chaine_ca_fi VARCHAR2(5000);
	l_ca_payeur VARCHAR2(5000); 
	chaine_ca_payeur VARCHAR2(5000);		
	l_compteur NUMBER;
	l_compteur2 NUMBER;
	l_perimo VARCHAR2(5000);
	p_where_perim VARCHAR2(5000); 
	l_menu VARCHAR2(25);
		
begin
-- Beg Modif QHL 10/8/2000
   select to_number(to_char(moismens, 'mm'))   into :p_MoisConso from datdebex;
   select to_number(to_char(moismens, 'yyyy')) into :p_AnConso   from datdebex;
-- End Modif QHL 10/8/2000

  -- des date en dure font parties du spec
  :P_DecAnNM1 := '01/12/' || to_char(:p_AnConso - 1);  
  :P_NovAnN   := '30/11/' || to_char(:p_AnConso);
	
	l_doss_proj := 'VIDE';
	l_projet := 'VIDE';
	l_application := 'VIDE';
	l_ca_fi := 'VIDE';
	chaine_doss_proj := 'VIDE';
	chaine_projet := 'VIDE';
	chaine_application := 'VIDE';
	chaine_ca_fi := 'VIDE';
	
	IF (:P_PARAM12 is not null) THEN
		:FIXE_2 := :P_PARAM12;
	END IF;
	
	IF (:FIXE_2 = 'MO') THEN
		:p_nom_etat := 'FACTINTG1 PAR MO';
	ELSE
		:p_nom_etat := 'FACTINTG1 PAR CA';
	END IF;
		
	:p_amort := 'NON AMORTISSABLES';
	
	 -- Informations pour l'habilitation par rfrentiels
	l_global := pack_global.lire_globaldata(:P_GLOBAL);

  :P_APPLI := l_global.appli;
  :P_APPLI := REPLACE(:P_APPLI,',',''',''');
  
	:P_DOSS_PROJ := l_global.doss_proj;
  
  :P_PROJET := l_global.projet;
  :P_PROJET := REPLACE(:P_PROJET,',',''',''');
  
  :P_CA_FI := l_global.ca_fi; 
  :P_CA_PAYEUR := l_global.ca_payeur;
	
IF(:FIXE_1 = 'REF') THEN
	
	if(:P_PARAM7 IS NOT NULL) THEN
		:P_PARAMDP := :P_PARAM7;
	END IF;

	if(:P_PARAM8 IS NOT NULL) THEN
		:P_PARAMAPPLI := :P_PARAM9;
	END IF;
	
	if(:P_PARAM9 IS NOT NULL) THEN
		:P_PARAMP := :P_PARAM8;
	END IF;
	
	if(:P_PARAM10 IS NOT NULL) THEN
		:P_PARAMCAFI := :P_PARAM10;
	END IF;
		
	if(:P_PARAM11 IS NOT NULL) THEN
		:P_PARAMCAPAYEUR := :P_PARAM11;
	END IF;	
				
  --srw.message(1,'ParamNom : '||:P_PARAMNOM);
  
  -- Dossier Projet :
	if(trim(:P_PARAM7) = 'Pas de limitation') then
		chaine_doss_proj := 'VIDE';
	elsif(trim(upper(:P_PARAM7)) = 'TOUS')	then
		if(:P_DOSS_PROJ IS NOT NULL AND :P_DOSS_PROJ <> 'TOUS' ) then
  		chaine_doss_proj := :P_DOSS_PROJ; -- seulement les dossiers projets actifs
		else
			chaine_doss_proj := 'VIDE';
		end if;	
	else
		chaine_doss_proj := :P_PARAM7;
	end if;	
  
  --srw.message(1,'chaine_doss_proj : '||chaine_doss_proj);
  
  -- Projet :
	if(trim(:P_PARAM8) = 'Pas de limitation') then
		chaine_projet := 'VIDE';
	elsif(trim(upper(:P_PARAM8)) = 'TOUS')	then
		if(:P_PROJET IS NOT NULL AND :P_PROJET <> 'TOUS' ) then
  		chaine_projet := :P_PROJET; -- seulement les projets actifs
		else
			chaine_projet := 'VIDE';	
		end if;	
	else
		chaine_projet := :P_PARAM8;
	end if;	

	--srw.message(1,'chaine_projet : '||chaine_projet);

	-- application :
	if(trim(:P_PARAM9) = 'Pas de limitation') then
		chaine_application := 'VIDE';
	elsif(trim(upper(:P_PARAM9)) = 'TOUS')	then
		if(:P_APPLI IS NOT NULL AND :P_APPLI <> 'TOUS' ) then
  		chaine_application := :P_APPLI; -- seulement les applications actifs
		else
			chaine_application := 'VIDE';	
		end if;	
	else
		chaine_application := :P_PARAM9;
	end if;	

	--srw.message(1,'chaine_application : '||chaine_application);

  -- CAFI
 	if(trim(:p_param10) = 'Pas de limitation') then
		chaine_ca_fi := '';
	elsif(trim(upper(:p_param10)) = 'TOUS')	then
		if(:P_CA_FI IS NOT NULL AND trim(upper(:P_CA_FI)) <> 'TOUS' ) then
  		chaine_ca_fi := :P_CA_FI;
		else
			chaine_ca_fi := '';	
		end if;	
	else
		chaine_ca_fi := :p_param10;
	end if;	

	--srw.message(1,'chaine_ca_fi : '||chaine_ca_fi);

  -- CA_PAYEUR
	if(trim(:p_param11) = 'Pas de limitation') then
		chaine_ca_payeur := '';
	elsif(trim(upper(:p_param11)) = 'TOUS')	then
		if(:P_CA_PAYEUR IS NOT NULL AND trim(upper(:P_CA_PAYEUR)) <> 'TOUS' ) then
  		chaine_ca_payeur := :P_CA_PAYEUR;
		else
			chaine_ca_payeur := '';	
		end if;	
	else
		chaine_ca_payeur := :p_param11;
	end if;	
	
	--srw.message(1,'chaine_ca_payeur : '||chaine_ca_payeur);
	
	if(chaine_doss_proj <> 'VIDE') then
		l_doss_proj := ' and bip.DPCODE IN (' || chaine_doss_proj ||') ';
	end if;
	
	if(chaine_projet <> 'VIDE') then 	
		l_projet := ' and bip.ICPI IN ('''||chaine_projet||''') ';
	end if;
	
	if(chaine_application <> 'VIDE') then
		l_application := ' and bip.AIRT IN ('''||chaine_application||''') ';
	end if;
	

	:p_from :='(';
	  l_compteur := 1 ;
       
     while (length(chaine_ca_payeur) != 0 and length(:p_from)<30000) loop
     
     if (instr(chaine_ca_payeur,',') != 0) then
          l_ca_payeur := substr(chaine_ca_payeur,0,instr(chaine_ca_payeur,',')-1);
          chaine_ca_payeur := substr(chaine_ca_payeur,instr(chaine_ca_payeur,',')+1);
     else
        l_ca_payeur := chaine_ca_payeur;
        chaine_ca_payeur := '';
     end if;

     if (l_compteur != 1) then
        :p_from := :p_from || 'UNION';

     end if;
     
      if (pack_global.recherche_niveau(l_ca_payeur) != 99) then
     
     if (pack_global.recherche_niveau(l_ca_payeur) = 0) then
   -- dans le cas de la facturation interne les mois pris en compte sont decembre N-1  novembre N
             	:p_from := :p_from||'(
                            select codcamo 
							from centre_activite
							where 
							codcamo IN ('|| l_ca_payeur || ')
							AND (cdateferm is null or cdateferm between to_date(''12/''||to_number(to_char(sysdate,''YYYY'')-1),''MM/YYYY'') and
last_day(to_date(''11/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))				 		         												
							)';	      
     elsif (pack_global.recherche_niveau(l_ca_payeur) = 1) then
            	
             :p_from := :p_from||'(						 		        
						     select codcamo 
						     from centre_activite
							 where 
								 CANIV1 IN ('|| l_ca_payeur || ')
							AND (cdateferm is null or cdateferm between to_date(''12/''||to_number(to_char(sysdate,''YYYY'')-1),''MM/YYYY'') and
last_day(to_date(''11/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))
						)';	      
           elsif (pack_global.recherche_niveau(l_ca_payeur) = 2) then
             :p_from := :p_from||' (						 		        
						 		        select codcamo 
													from centre_activite
													where 
														CANIV2 IN ('|| l_ca_payeur|| ')
												AND (cdateferm is null or cdateferm between to_date(''12/''||to_number(to_char(sysdate,''YYYY'')-1),''MM/YYYY'') and
last_day(to_date(''11/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))
											)';
            elsif (pack_global.recherche_niveau(l_ca_payeur) = 3) then
              :p_from := :p_from||' (						 		        
						 		        select codcamo 
													from centre_activite
													where 
												 			CANIV3 IN ('|| l_ca_payeur || ')
												AND (cdateferm is null or cdateferm between to_date(''12/''||to_number(to_char(sysdate,''YYYY'')-1),''MM/YYYY'') and
last_day(to_date(''11/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))
												)';	 
                elsif (pack_global.recherche_niveau(l_ca_payeur) = 4) then       
                :p_from :=:p_from|| '
					 		         (						 		        
						 		        select codcamo 
													from centre_activite
													where 
																CANIV4 IN ('|| l_ca_payeur || ')
												AND (cdateferm is null or cdateferm between to_date(''12/''||to_number(to_char(sysdate,''YYYY'')-1),''MM/YYYY'') and
last_day(to_date(''11/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))
												)';
									elsif (pack_global.recherche_niveau(l_ca_payeur) = 5) then       
                :p_from :=:p_from|| '
					 		         (						 		        
						 		        select codcamo 
													from centre_activite
													where 
																CANIV5 IN ('|| l_ca_payeur || ')
												AND (cdateferm is null or cdateferm between to_date(''12/''||to_number(to_char(sysdate,''YYYY'')-1),''MM/YYYY'') and
last_day(to_date(''11/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))
												)';
									elsif (pack_global.recherche_niveau(l_ca_payeur) = 6) then       
                :p_from :=:p_from|| '
					 		         (						 		        
						 		        select codcamo 
													from centre_activite
													where 
																CANIV6 IN ('|| l_ca_payeur || ')
												AND (cdateferm is null or cdateferm between to_date(''12/''||to_number(to_char(sysdate,''YYYY'')-1),''MM/YYYY'') and
last_day(to_date(''11/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))
											)';
												
                         	
          end if;          
        l_compteur := l_compteur+1;
       end if;
      END LOOP;      
    
    	:p_from := :p_from || ')';
    	
  
    	
-- CAFI MULTI NIVEAU

	:p_from2 :='(';
	  l_compteur2 := 1 ;
       
     while (length(chaine_ca_fi) != 0 and length(:p_from2)<30000) loop
     
     if (instr(chaine_ca_fi,',') != 0) then
          l_ca_fi := substr(chaine_ca_fi,0,instr(chaine_ca_fi,',')-1);
          chaine_ca_fi := substr(chaine_ca_fi,instr(chaine_ca_fi,',')+1);
     else
        l_ca_fi := chaine_ca_fi;
        chaine_ca_fi := '';
     end if;

     if (l_compteur2 != 1) then
        :p_from2 := :p_from2 || 'UNION';

     end if;
     
      if (pack_global.recherche_niveau(l_ca_fi) != 99) then
     if (pack_global.recherche_niveau(l_ca_fi) = 0) then
   -- dans le cas de la facturation interne les mois pris en compte sont decembre N-1  novembre N
             	:p_from2 := :p_from2||'(
                            select codcamo 
							from centre_activite
							where 
							codcamo IN ('|| l_ca_fi || ')
							AND (cdateferm is null or cdateferm between to_date(''12/''||to_number(to_char(sysdate,''YYYY'')-1),''MM/YYYY'') and
last_day(to_date(''11/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))				 		         												
							)';	      
     elsif (pack_global.recherche_niveau(l_ca_fi) = 1) then
            	
             :p_from2 := :p_from2||'(						 		        
						     select codcamo 
						     from centre_activite
							 where 
								 CANIV1 IN ('|| l_ca_fi || ')
							AND (cdateferm is null or cdateferm between to_date(''12/''||to_number(to_char(sysdate,''YYYY'')-1),''MM/YYYY'') and
last_day(to_date(''11/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))
							)';	      
           elsif (pack_global.recherche_niveau(l_ca_fi) = 2) then
             :p_from2 := :p_from2||' (						 		        
						 		        select codcamo 
													from centre_activite
													where 
														CANIV2 IN ('|| l_ca_fi|| ')
												AND (cdateferm is null or cdateferm between to_date(''12/''||to_number(to_char(sysdate,''YYYY'')-1),''MM/YYYY'') and
last_day(to_date(''11/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))
												)';
            elsif (pack_global.recherche_niveau(l_ca_fi) = 3) then
              :p_from2 := :p_from2||' (						 		        
						 		        select codcamo 
													from centre_activite
													where 
												 			CANIV3 IN ('|| l_ca_fi || ')
												AND (cdateferm is null or cdateferm between to_date(''12/''||to_number(to_char(sysdate,''YYYY'')-1),''MM/YYYY'') and
last_day(to_date(''11/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))
												)';	 
                elsif (pack_global.recherche_niveau(l_ca_fi) = 4) then       
                :p_from2 :=:p_from2|| '
					 		         (						 		        
						 		        select codcamo 
													from centre_activite
													where 
																CANIV4 IN ('|| l_ca_fi || ')
												AND (cdateferm is null or cdateferm between to_date(''12/''||to_number(to_char(sysdate,''YYYY'')-1),''MM/YYYY'') and
last_day(to_date(''11/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))
												)';
									elsif (pack_global.recherche_niveau(l_ca_fi) = 5) then       
                :p_from2 :=:p_from2|| '
					 		         (						 		        
						 		        select codcamo 
													from centre_activite
													where 
																CANIV5 IN ('|| l_ca_fi || ')
												AND (cdateferm is null or cdateferm between to_date(''12/''||to_number(to_char(sysdate,''YYYY'')-1),''MM/YYYY'') and
last_day(to_date(''11/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))
												)';
									elsif (pack_global.recherche_niveau(l_ca_fi) = 6) then       
                :p_from2 :=:p_from2|| '
					 		         (						 		        
						 		        select codcamo 
													from centre_activite
													where 
																CANIV6 IN ('|| l_ca_fi || ')
												AND (cdateferm is null or cdateferm between to_date(''12/''||to_number(to_char(sysdate,''YYYY'')-1),''MM/YYYY'') and
last_day(to_date(''11/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))
												)';
												
                           	
          end if;          
        l_compteur2 := l_compteur2+1;
        end if;
      END LOOP;      
    
    	:p_from2 := :p_from2 || ')';
    	

-- FIN CAFI MULTI NIVEAU    	
						
 	
    	
    		if ((:p_from = '()' and :p_from2 = '()') or ((length(:p_from)+length(:p_from2) >= 30000 ))) then
    	:p_where := 'from synthese_fin sf,
											 client_mo cli,
											 ligne_bip bip,
											 centre_activite ca, 
											 datdebex d
										 where bip.PID = sf.PID
											 and bip.CLICODE = cli.CLICODE
											 and sf.CODCAMO = ca.CODCAMO
											 and sf.ANNEE = to_char(d.MOISMENS,''YYYY'')
											 ';
											 
			elsif (:p_from <> '()' and :p_from2 = '()') then
						:p_where := 'from synthese_fin sf,
											 client_mo cli,
											 ligne_bip bip,
											 '||:p_from||' liste_ca	, 
											 datdebex d ,
											 centre_activite ca
										 where bip.PID = sf.PID
											 and bip.CLICODE = cli.CLICODE
											 and ca.codcamo = liste_ca.CODCAMO
											 and sf.CODCAMO = liste_ca.CODCAMO
											 and sf.ANNEE = to_char(d.MOISMENS,''YYYY'')';
				
					elsif (:p_from = '()' and :p_from2 <> '()') then
							:p_where := 'from synthese_fin sf,
											 client_mo cli,
											 ligne_bip bip,
											 '||:p_from2||' liste_fi	, 
											 datdebex d ,
											 centre_activite ca
										 where bip.PID = sf.PID
											 and bip.CLICODE = cli.CLICODE
											 and sf.codcamo = ca.CODCAMO
											 and sf.cafi = liste_fi.CODCAMO
											 and sf.ANNEE = to_char(d.MOISMENS,''YYYY'')';
							
									
				
				
							elsif (:p_from <> '()' and :p_from2 <> '()') then
				
									:p_where := 'from synthese_fin sf,
											 client_mo cli,
											 ligne_bip bip,
											 '||:p_from||' liste_ca	, 
											 '||:p_from2||' liste_fi	,
											 datdebex d ,
											 centre_activite ca
										 where bip.PID = sf.PID
											 and bip.CLICODE = cli.CLICODE
											 and ca.codcamo = liste_ca.CODCAMO
											 and sf.CODCAMO = liste_ca.CODCAMO
											 and sf.cafi = liste_fi.CODCAMO
											 and sf.ANNEE = to_char(d.MOISMENS,''YYYY'')';
		
											 											 											 
			end if;	
    	
    	
    	
    	
    	
    	
    	
			
		--srw.message(1,'l_ca_fi : '||l_ca_fi);	
		--srw.message(1,'l_projet : '||l_projet);		
		--srw.message(1,'l_doss_proj : '||l_doss_proj);
		--srw.message(1,'l_application : '||l_application);
			
		
			
			if(l_doss_proj <> 'VIDE') then
				:p_where := :p_where||l_doss_proj;
			end if;
			
			if(l_projet <> 'VIDE') then
				:p_where := :p_where||l_projet;
			end if;
			
			if(l_application <> 'VIDE') then
				:p_where := :p_where||l_application;
			end if;
 			 			
 		--srw.message(1,NVL(:P_WHERE,'vide'));
			
END IF;

IF(:FIXE_1 = 'MO') THEN

		if(:P_PARAM6 IS NOT NULL) THEN
			:P_PARAMCAPAYEUR := :P_PARAM6;
		END IF;	


		/*CMA Fiche 1176 09/05/2011 le perimo est diffrent si l'utilisateur est dans le menu Client*/
		l_menu := pack_global.lire_menutil(:P_global);
		
		if('MO'=l_menu)then
			l_perimo := pack_global.lire_perimcli(:P_global);
		else
			l_perimo := pack_global.lire_perimo(:P_global);
		end if;
		
		p_where_perim := ' ';
	  
	  if length(l_perimo) != 0 then	  	
				p_where_perim := ' and ( bip.clicode in (select clicode from vue_clicode_perimo where BDCLICODE in ('||l_perimo||') ) 
																or bip.clicode_oper in (select clicode from vue_clicode_perimo where BDCLICODE in ('||l_perimo||') ) )';
		end if;
	  	
		if(:p_param6 = 'Pas de limitation') then
			chaine_ca_payeur := '';
		elsif(:p_param6 = 'Tous')	then
			if(:P_CA_PAYEUR IS NOT NULL AND :P_CA_PAYEUR <> 'Tous' ) then
	  		chaine_ca_payeur := :P_CA_PAYEUR;
			else
				chaine_ca_payeur := '';	
			end if;	
		else
			chaine_ca_payeur := :p_param6;
		end if;	
	
  :p_from :='(';	
  l_compteur := 1 ;
	
 	while (length(chaine_ca_payeur) != 0  and length(:p_from)<30000) loop
     
     if (instr(chaine_ca_payeur,',') != 0) then
          l_ca_payeur := substr(chaine_ca_payeur,0,instr(chaine_ca_payeur,',')-1);
          chaine_ca_payeur := substr(chaine_ca_payeur,instr(chaine_ca_payeur,',')+1);
     else
        l_ca_payeur := chaine_ca_payeur;
        chaine_ca_payeur := '';
     end if;
     
    
     
        if (l_compteur != 1) then
            :p_from := :p_from || 'UNION';
    
        end if;
        
         if (pack_global.recherche_niveau(l_ca_payeur) != 99) then
     
     if (pack_global.recherche_niveau(l_ca_payeur) = 0) then
   -- dans le cas de la facturation interne les mois pris en compte sont decembre N-1  novembre N
   
             	:p_from := :p_from||'(
                            select codcamo 
							from centre_activite
							where 
							codcamo IN ('|| l_ca_payeur || ')
							AND (cdateferm is null or cdateferm between to_date(''12/''||to_number(to_char(sysdate,''YYYY'')-1),''MM/YYYY'') and
last_day(to_date(''11/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))				 		         												
							)';	 
							
      
     elsif (pack_global.recherche_niveau(l_ca_payeur) = 1) then
            	
                :p_from := :p_from||'(						 		        
						     select codcamo 
						     from centre_activite
							 where 
								 CANIV1 IN ('|| l_ca_payeur || ')
							AND (cdateferm is null or cdateferm between to_date(''12/''||to_number(to_char(sysdate,''YYYY'')-1),''MM/YYYY'') and
last_day(to_date(''11/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))
							)';	      
           elsif (pack_global.recherche_niveau(l_ca_payeur) = 2) then
             :p_from := :p_from||' (						 		        
						 		        select codcamo 
													from centre_activite
													where 
														CANIV2 IN ('|| l_ca_payeur|| ')
												AND (cdateferm is null or cdateferm between to_date(''12/''||to_number(to_char(sysdate,''YYYY'')-1),''MM/YYYY'') and
last_day(to_date(''11/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))
												)';
               elsif (pack_global.recherche_niveau(l_ca_payeur) = 3) then
                :p_from := :p_from||' (						 		        
						 		        select codcamo 
													from centre_activite
													where 
												 			CANIV3 IN ('|| l_ca_payeur || ')
												AND (cdateferm is null or cdateferm between to_date(''12/''||to_number(to_char(sysdate,''YYYY'')-1),''MM/YYYY'') and
last_day(to_date(''11/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))
													)';	 
                elsif (pack_global.recherche_niveau(l_ca_payeur) = 4) then       
                :p_from :=:p_from|| '
					 		         (						 		        
						 		        select codcamo 
													from centre_activite
													where 
																CANIV4 IN ('|| l_ca_payeur || ')
												AND (cdateferm is null or cdateferm between to_date(''12/''||to_number(to_char(sysdate,''YYYY'')-1),''MM/YYYY'') and
last_day(to_date(''11/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))
													)';
												
					elsif (pack_global.recherche_niveau(l_ca_payeur) = 5) then       
                :p_from :=:p_from|| '
					 		         (						 		        
						 		        select codcamo 
													from centre_activite
													where 
																CANIV5 IN ('|| l_ca_payeur || ')
												AND (cdateferm is null or cdateferm between to_date(''12/''||to_number(to_char(sysdate,''YYYY'')-1),''MM/YYYY'') and
last_day(to_date(''11/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))
												)';		
												
				elsif (pack_global.recherche_niveau(l_ca_payeur) = 6) then       
                :p_from :=:p_from|| '
					 		         (						 		        
						 		        select codcamo 
													from centre_activite
													where 
																CANIV6 IN ('|| l_ca_payeur || ')
												AND (cdateferm is null or cdateferm between to_date(''12/''||to_number(to_char(sysdate,''YYYY'')-1),''MM/YYYY'') and
last_day(to_date(''11/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))
													)';																							
				            	
          end if;
           
        l_compteur := l_compteur+1;
       end if;
      END LOOP;      
    
    :p_from := :p_from || ')';     
    		
		if (:p_from = '()' or length(:p_from)>=30000) then
    		:p_where := 'from synthese_fin sf,
											 client_mo cli,
											 ligne_bip bip,
											 centre_activite ca, 
											 datdebex d
										 where bip.PID = sf.PID
											 and bip.CLICODE = cli.CLICODE
											 and sf.CODCAMO = ca.CODCAMO
											 and sf.ANNEE = to_char(d.MOISMENS,''YYYY'')'
											 ||p_where_perim;
    else
    		:p_where := 'from synthese_fin sf,
											 client_mo cli,
											 ligne_bip bip,
											 '||:p_from||' liste_ca	, 
											 datdebex d,
											 centre_activite ca
										 where bip.PID = sf.PID
											 and bip.CLICODE = cli.CLICODE
											 and ca.codcamo = liste_ca.CODCAMO
											 and sf.CODCAMO = liste_ca.CODCAMO
											 and sf.ANNEE = to_char(d.MOISMENS,''YYYY'')'
											 ||p_where_perim;
    end if;	
 	
 	--srw.message(1,NVL(:P_WHERE,'vide'));
 	
END IF;
		--srw.message(1,'where : '||:P_WHERE);
  return (TRUE);
end;

