function.textSource:
      function CF_LibDepPoleFormula return VARCHAR2 is
begin
     return pack_utile.f_get_deppole(:dc_coddeppole);
end;

      function AfterPForm return boolean is
begin
   IF :P_PARAM3 IS NOT NULL THEN 
	:p_where := ' and C.FILCODE = ''' || :P_PARAM3 ||'''';
   END IF;


  IF (:P_PARAM6 IS NOT NULL) and (:P_PARAM7 IS NOT NULL) THEN 
-- Fact.enregistrees	
	:p_whereEnr :=  ' and f.fenrcompta >= TO_DATE(''' || :P_PARAM6 || ''',''dd/mm/yyyy'')';
        :p_whereEnr := :P_whereEnr || ' and f.fenrcompta <= TO_DATE(''' || :P_PARAM7 || ''',''dd/mm/yyyy'')';

--:p_whereEnr :=  ' and f.fdatsai >= TO_DATE(''' || :P_PARAM6 || ''',''dd/mm/yyyy'')';
--:p_whereEnr := :P_whereEnr || ' and f.fdatsai <= TO_DATE(''' || :P_PARAM7 || ''',''dd/mm/yyyy'')';

--Fact. reglees : FREGCOMPTA ou FACCSEC dans la periode selectionnee
	:p_whereReg := ' AND((f.fregcompta >= TO_DATE(''' || :P_PARAM6 || ''',''dd/mm/yyyy'')';
	:p_whereReg := :P_whereReg || ' and f.fregcompta <= TO_DATE(''' || :P_PARAM7 || ''',''dd/mm/yyyy''))';
        :p_whereReg := :P_whereReg || ' OR ( f.faccsec >= TO_DATE(''' || :P_PARAM6 || ''',''dd/mm/yyyy'')';
        :p_whereReg := :P_whereReg || ' and f.faccsec <= TO_DATE(''' || :P_PARAM7 || ''',''dd/mm/yyyy'')))';

-- exclusion des factures en code 8
	:p_wherecode8 := 'AND f.fmodreglt <> 8';


  END IF;
  IF   (:P_PARAM5!='0') AND (:P_PARAM5 is not null) THEN
	:p_wherecf := ' and to_char(f.fcentrefrais) = ''' || :P_PARAM5 ||'''';
  ELSE
	:p_wherecf := ' and 1=1 ';
  END IF;
 
--  --srw.message(1,:p_whereEnr );
--  --srw.message(1,:p_whereReg );
-- --srw.message(1,:p_wherecf );
  return (TRUE);
end;

      function BeforeReport return boolean is
begin
  :P_PERIODE := 'PERIODE DU ' || :P_PARAM6 || ' AU ' || :P_PARAM7;
  return (TRUE);
end;

      function CF_LibDepFormula return VARCHAR2 is
begin
  return 'TOTAL ' || substr(pack_utile.f_get_deppole(:dc_coddeppole),1,3);
end;

      function F_Filiale1FormatTrigger return boolean is
Text filiale_cli.filsigle%type;
begin
  IF :P_PARAM3 IS NOT NULL THEN
	 SELECT filsigle INTO Text FROM filiale_cli WHERE filcode = :P_PARAM3;
	 Text := :P_PARAM3 || ' ' ||Text;
	 SRW.SET_FIELD_CHAR(0,Text);
  END IF;
  return (TRUE);
end;

      function F_CFRAIS1FormatTrigger return boolean is
Text varchar2(60);
begin
  if NVL(LENGTH(:P_PARAM5), 0)!= 0 then 
	 Select LIBCFRAIS into Text from centre_frais where to_char(codcfrais) = :P_PARAM5;
	 Text := :P_PARAM5 || ' - ' ||Text;
	 SRW.SET_FIELD_CHAR(0,Text);
  end if;
  return (TRUE);
end;
