function.textSource:
      function AfterPForm return boolean is
begin
  	:P_WHERE := '';
	IF(:P_PARAM6 is not null AND :P_PARAM7 is not null)THEN
		
		:P_WHERE := :P_WHERE || ' AND (UPPER(de.CODE_ACTION) BETWEEN UPPER('''||:P_PARAM6||''') AND UPPER('''||:P_PARAM7||''') OR
		UPPER(de.CODE_ACTION) LIKE UPPER('''||:P_PARAM6||'%'') OR
		UPPER(de.CODE_ACTION) LIKE UPPER('''||:P_PARAM7||'%'')) ';
		
	ELSIF (:P_PARAM6 is null AND :P_PARAM7 is not null) THEN
		
			:P_WHERE := :P_WHERE || ' AND (UPPER(de.CODE_ACTION) <= UPPER('''||:P_PARAM7||''') OR
			UPPER(de.CODE_ACTION) LIKE UPPER('''||:P_PARAM7||'%'')) ';
		
	ELSIF(:P_PARAM7 is null AND :P_PARAM6 is not null) THEN
		
			:P_WHERE := :P_WHERE || ' AND (UPPER(de.CODE_ACTION) >= UPPER('''||:P_PARAM6||''') OR
			UPPER(de.CODE_ACTION) LIKE UPPER('''||:P_PARAM6||'%'')) ';
		
	END IF;
	
	IF (:P_PARAM6 is null) THEN
		:P_PARAM6 := '(vide)';
	END IF;
	
	IF (:P_PARAM7 is null) THEN
		:P_PARAM7 := '(vide)';
	END IF;
	
	IF(:P_PARAM8='ACTIVES') THEN
		
			:P_WHERE := :P_WHERE ||' AND lpb.ACTIF=''O'' ';
		
	END IF;
	
	  --srw.message(1,'WHERE : '||:P_WHERE);
  return (TRUE);
end;
