      function AfterPForm return boolean is
   l_annee0  VARCHAR2(4);
   l_annee1  VARCHAR2(4);
   l_param   VARCHAR2(40);
   l_conso number(12,2);
   l_annee_num NUMBER(4);  
   l_retour NUMBER; 
   l_fin_requete VARCHAR2(200) ; 
   --l_perimo VARCHAR2(500);
   
   --infos utilisateur (matricule,branche) + date du jour : timestamp
   l_identifiant NUMBER(5) ;  
	-- l_libelle_branche VARCHAR2(30); 
	-- l_coddir NUMBER(2); 
	-- l_codbr NUMBER(2); 
	 l_codsg_utilisateur NUMBER(7) ;   
	 
begin
				 	  
   -- ================================================
   -- Determiner l'annee de traitement N et N+1
   -- ================================================
   SELECT TO_CHAR(moismens,'YYYY') INTO l_annee0 
   FROM datdebex;
   
   l_annee1 := to_char(to_number(l_annee0,'FM9999') + 1,'FM9999');
   :p_where := ' and budg.annee(+) = ' || l_annee0 ;
   :p_where := :p_where || ' and budg1.annee(+) = ' || l_annee1 ;
   :p_where := :p_where || ' and conso.annee(+) = ' || l_annee0 ;
   -- ================================================
   -- Soustraire les eventuels $ ou ' ' dans p_param6
   -- ================================================
    l_fin_requete := ' ';
   IF NVL(LENGTH(LPAD(:P_PARAM6, 7, '0')), 0)!= 0 then 
        l_param := rtrim(rtrim(LPAD(:P_PARAM6, 7, '0'),'*')) ;
      	:p_where := :p_where || ' and  TO_CHAR(lb.codsg, ''FM0000000'') like ''' || l_param ||'%'' ';
      	l_fin_requete := ' and  TO_CHAR(lb.codsg, ''FM0000000'') like ''' || l_param ||'%'' ';
   END IF;
  
	--	l_perimo := pack_global.lire_perimo(:p_global);
		
	--	if length(l_perimo) != 0 AND '000000000' not in (l_perimo) AND '099988888' not in (l_perimo) then	  	
	--			:P_WHERE := :p_where || ' and ( lb.clicode in (select clicode from vue_clicode_perimo where BDCLICODE in ('||l_perimo||') ) 
	--															or lb.clicode_oper in (select clicode from vue_clicode_perimo where BDCLICODE in ('||l_perimo||') ) )';
	--	end if;
			
   l_annee_num := TO_NUMBER( l_annee0 ) ; 
 --srw.message(1, ' l_annee_num : ' || l_annee_num );
 --srw.message(1, ' P_PARAM6 : ' || :P_PARAM6 );
  --srw.message(1, ' p_param14 : ' || :p_param14 );
      
   :P_NUMSEQ := pack_cout_standard_ke.insert_tmp_coutke_etats(l_annee_num,:P_PARAM6,NULL,NULL,NULL,:p_param14, :P_GLOBAL);
--srw.message(1, 'P_NUMSEQ : ' || :P_NUMSEQ);
  return (TRUE);
end;

      function CF_Annee0 return number is
  l_annee varchar2(4);
begin
  select to_char(moismens,'YYYY') into l_annee from datdebex;
  return to_number(l_annee,'FM9999');
end;

      function CF_AnN1 return Number is
  l_annee varchar2(4);
begin
  select to_char(moismens,'YYYY') into l_annee from datdebex;
  return to_number(l_annee,'FM9999') + 1;
end;

      function CF_MoisAnneeFormula return Date is
  l_annee date;
begin
  select moismens into l_annee from datdebex; 
  return l_annee;
end;

      function CF_AnN2Formula return Number is
 l_annee varchar2(4);
begin
