      function AfterPForm return boolean is

   l_annee0  VARCHAR2(10);
   l_annee1  VARCHAR2(10);
   l_param   VARCHAR2(40);
   l_conso   number(12,2);
   l_annee_num NUMBER(4); 
   --l_perimo VARCHAR2(500);   
   
      --infos utilisateur (matricule,branche) + date du jour : timestamp
   l_identifiant NUMBER(5) ;  
	 --l_libelle_branche VARCHAR2(30); 
	-- l_coddir NUMBER(2); 
	-- l_codbr NUMBER(2); 
	 l_codsg_utilisateur NUMBER(7) ;   
	 
begin
	
	
	-- Rcupration des donnes utilisateur : 
	 	-- p_param6 contient dpg utilisateur
/*	 	  
	 	  l_codbr := NULL ; 
	 	  l_libelle_branche := NULL ; 
	 	  IF(:p_param6 IS NOT NULL AND LENGTH(:p_param6)>0 ) THEN 
	 	  BEGIN   
