function.textSource:
      function CF_TOT_SG_GRATUITFormula return Number is
begin
  RETURN :CS_TOTAL_SG_GRA + :CS_TOTAL_SG_INT + :CS_TOTAL_SG_STA + :CS_TOTAL_IFO;
end;

      function CF_TOTAL_SSIIFormula return Number is
l_total number(10);
begin
l_total := :CS_SSII - (:CS_TOT_GRATUIT - :CF_TOT_SG_GRATUIT);
  
  return l_total;

end;

      function CF_TOTAL_SGFormula return Number is
l_total number(10);

begin
  l_total := :CS_SG - :CF_TOT_SG_GRATUIT;
  
  return l_total;
end;

      function CF_TOTAL_RESSFormula return Number is
l_total number;
begin
  l_total := :CF_TOTAL_SG + :CF_TOTAL_SSII + :CS_TOT_GRATUIT;
  
  return l_total;
end;

      function M_PERSFormatTrigger return boolean is
-- CAS 2 : se rapporte  la requete Q_POLE
-- si on coche par pole cad P_PARAM7=2
-- si on coche par departement cad P_PARAM7=1 mais que l'on entre un Dep/Pole **
begin
	
	if INSTR(:P_Param6,'*',1)=0  then
    	return (FALSE);
   		elsif (:P_Param7=2) OR (:P_Param7=1 AND INSTR(:P_Param6,'*',1)=6)  then
    			return (TRUE);
   		else
   			return (FALSE);
   end if;
end;

      function AfterPForm return boolean is

	l_param VARCHAR2(7);
	l_tmp VARCHAR2(20);
	l_tmp2 VARCHAR2(20);
	l_perim_me	VARCHAR2(1000) := '';
	
begin
  
:p_where :='';
:p_where1 :='';
:p_where2 :='';

	-- Filtre du perim ME
	l_perim_me := pack_global.lire_perime(:P_GLOBAL);		
	:p_where :=' AND sit.codsg IN (SELECT codsg FROM vue_dpg_perime where INSTR('''|| l_perim_me ||''', codbddpg) > 0 )'; 
	:p_where1 :=' AND cont.codsg IN (SELECT codsg FROM vue_dpg_perime where INSTR('''|| l_perim_me ||''', codbddpg) > 0 )'; 
	:p_where2 :=' AND pro.codsg IN (SELECT codsg FROM vue_dpg_perime where INSTR('''|| l_perim_me ||''', codbddpg) > 0 )'; 

	-- Filtre sur code DPG generique qc 1281
	if LPAD(:P_PARAM6, 7, '*') = '*******' then
			l_param := '';
	else
		-- ajout de '0' a gauche sur 7 caracteres + suppression des '*'
	  l_param := rtrim(rtrim(LPAD(:P_PARAM6, 7, '0'),'*')) ;

	end if;	
	:p_where := :p_where   || ' and  TO_CHAR(sit.codsg, ''FM0000000'') like ''' || l_param || '%'' ';
	:p_where1 := :p_where1 || ' and  to_char(cont.codsg,''FM0000000'') like ''' || l_param || '%'' ';
	:p_where2 := :p_where2 || ' and  to_char(pro.divsecgrou,''FM0000000'') like ''' || l_param || '%'' ';
  
   if (:p_param8 != 'TOUS')then
   	-- on retire la dernire virgule  la fin de la chaine :p_param8
   	:p_param8 := substr(:p_param8,1,length(:p_param8)-1);
   	 --srw.message(1,:p_param8); 
   	if ((instr(:p_param8,'/A/') <> 0 ) and (instr(:p_param8,'/P/') = 0 ) and (instr(:p_param8,'/E/') = 0 ) and (instr(:p_param8,'/F/') = 0 )
   		and (instr(:p_param8,'/L/') = 0 ))
   		then
   			 --srw.message(1,'antoine1'); 
   		:p_where := :p_where || 'and sit.soccode = ''SG..''';
  		:p_where := :p_where || 'and res.rtype = ''P''';
   	elsif (instr(:p_param8,'/A/') = 0) then
   		--srw.message(1,'antoine2'); 
  		select replace(replace(:p_param8,'/A/,',''),'/','''') into l_tmp  from dual;
  		 --srw.message(1,:p_param8); 
  		  --srw.message(1,l_tmp); 
  	  :p_where := :p_where || 'and sit.soccode <> ''SG..''';
  	     --srw.message(1,:P_where); 
  		:p_where := :p_where || 'and res.rtype in ('|| l_tmp ||')';
  		   --srw.message(1,:P_where); 
   	else
   		 		--srw.message(1,'antoine3'); 
   		select replace(:p_param8,'/','''') into l_tmp from dual;
   		--srw.message(1,l_tmp); 
   		l_tmp := substr(l_tmp,5,length(l_tmp));
   			--srw.message(1,l_tmp); 
   		:p_where := :p_where || 'and (res.rtype in ('|| l_tmp ||') or (sit.soccode = ''SG..'' and res.rtype  = ''P''))';
     end if;
   end if;
  
    --srw.message(1,:P_where); 
    
 return (true);
  end;

