      function R_liste_nomFormatTrigger return boolean is
reste number;
begin
reste := mod(:CS_NUM,2);
	if reste=0 then
-- manip pour alterner les couleurs
    srw.attr.mask := SRW.BFCOLOR_ATTR;
 srw.attr.bfcolor :='white';
 srw.set_attr (0,srw.attr);
end if;
  return (TRUE);
end;

      function R_liste_nom_gFormatTrigger return boolean is
reste number;
begin
reste := mod(:CS_NUM_g,2);
	if reste=0 then
-- manip pour alterner les couleurs
    srw.attr.mask := SRW.BFCOLOR_ATTR;
 srw.attr.bfcolor :='white';
 srw.set_attr (0,srw.attr);
end if;
  return (TRUE);
end;

      function M_GROUPEFormatTrigger return boolean is
-- CAS 1 : pas  d etoile dans le dpg ou param7  3
-- se rapporte  la requete Q_GROUPE
begin
     
   if INSTR(:P_Param6,'*',1)=0  then
    	return (TRUE);
   elsif :P_Param7=3 or :P_Param7 is null then
    	return (TRUE);
   else
    	return (FALSE);
   end if;
end;

      function R_liste_nom1FormatTrigger return boolean is
reste number;
begin
reste := mod(:CS_NUM1,2);
	if reste=0 then
-- manip pour alterner les couleurs
    srw.attr.mask := SRW.BFCOLOR_ATTR;
 srw.attr.bfcolor :='white';
 srw.set_attr (0,srw.attr);
end if;
  return (TRUE);
end;

      function M_DEPFormatTrigger return boolean is
-- CAS 1 : se rapporte  la requete Q_DEP
-- si on coche par departement cad P_PARAM7=1
-- si on coche par departement cad P_PARAM7=1 mais que l'on entre un Dep/Pole/** : par pole
begin
	
	if INSTR(:P_Param6,'*',1)=0  then
    	return (FALSE);
   		elsif (:P_Param7=1 AND substr(LPAD(:P_PARAM6, 7, '0'),4,4)='****')   then
    			return (TRUE);
   		else
   			return (FALSE);
   		end if;
end;

