function.textSource:
      function M_GROUPEFormatTrigger return boolean is
-- CAS 1 : pas  d etoile dans le dpg ou param7 à G
-- se rapporte à la requete Q_LISTEL_GROUPE
begin
  
    	return (TRUE);
end;

      function AfterPForm return boolean is
l_codsg VARCHAR2(7);
begin
  
:p_where :='';
:p_where1 :='';
:p_where2 :='';

	if LPAD(:P_PARAM6, 7, '*')='*******' then
			l_codsg := '';
	else
		if substr(LPAD(:P_PARAM6, 7, '0'),4,4)='****' then
			l_codsg := substr(LPAD(:P_PARAM6, 7, '0'),1,3);
		else
			if substr(LPAD(:P_PARAM6, 7, '0'),6,2)='**' then
					l_codsg := substr(LPAD(:P_PARAM6, 7, '0'),1,5);
			else
					l_codsg := LPAD(:P_PARAM6, 7, '0');
		  end if;
		end if;
	end if;		
		:p_where := 'and  to_char(sit.codsg,''FM0000000'') like ''' || l_codsg ||'%'' ';
		:p_where1 := 'and  to_char(cont.codsg,''FM0000000'') like ''' || l_codsg ||'%'' ';
		:p_where2 := 'and  to_char(pro.divsecgrou,''FM0000000'') like ''' || l_codsg ||'%'' ';
		
	
 
  --srw.message(1,:P_where);  
  return (true);
  end;

      function CF_TOT_SG_GRATUIT_gFormula return Number is
begin
  RETURN :CS_TOTAL_SG_GRA_g + :CS_TOTAL_SG_INT_g + :CS_TOTAL_SG_STA_g + :CS_TOTAL_IFO_g;
end;

      function CF_TOTAL_SG_gFormula return Number is
l_total number;
begin
  l_total := :CS_SG_g - :CF_TOT_SG_GRATUIT_g;
  return l_total;
end;

      function CF_TOTAL_SSII_gFormula return Number is
l_total number;
begin
  l_total := :CS_SSII_g - (:CS_TOT_GRATUIT_g - :CF_TOT_SG_GRATUIT_g);
  
  return l_total;

end;

      function CF_TOTAL_RESS_gFormula return Number is
l_total number;
begin
  l_total := :CF_TOTAL_SG_g + :CF_TOTAL_SSII_g + :CS_TOT_GRATUIT_g;
  
  return l_total;
end;

      function M_liste_nom3FormatTrigger return boolean is
reste number;
begin
reste := mod(:CS_NUM_g,2);
	if reste=0 then
-- manip pour alterner les couleurs
    srw.attr.mask := SRW.BFCOLOR_ATTR;
 srw.attr.bfcolor :='white';
 srw.set_attr (0,srw.attr);
end if;
  return (TRUE);
end;
