      function CF_LIB_TYPE_RESSFormula return VARCHAR2 is
	l_res VARCHAR2(20) := ' ';
begin
  	IF    ( :dc_codqualif = '2' ) THEN
		l_res := 'non facturé';
	ELSE
		l_res := 'facturé';
	END IF;
	
	RETURN(l_res);
end;

