function.textSource:
      function AfterPForm return boolean is

car1 varchar2(1);
car2 varchar2(1);
car3 varchar2(1);
car4 varchar2(1);
car5 varchar2(1);
car6 varchar2(1);
car7 varchar2(1);
l_perim_me varchar2(2000);


begin

l_perim_me := pack_global.lire_perime(:P_GLOBAL);

:p_where :=' AND L.codsg IN (SELECT codsg FROM vue_dpg_perime where INSTR('''||l_perim_me||''', codbddpg) > 0 )';

if (:p_param6 is not null) or (rtrim(:p_param6)!= ' ') then
	:p_where := :p_where || 'and L.codpspe like '||''''||rtrim(substr(:p_param6,1,1))|| '%''';
end if;

IF LPAD(:p_param7, 7, '*')='*******' then
	IF :p_param9!='**' then --toute la direction
		:p_where :=  :p_where ||' and S.coddir=to_number(:p_param9)';
	ELSE
		IF :p_param8!='**' then --toute la branche
			:p_where :=  :p_where ||' and D.codbr=to_number(:p_param8)';
		ELSE --toute la BIP
			null;
		END IF;
	END IF;

ELSE
	select decode(substr(LPAD(:p_param7, 7, '0'),1,1),'*','_',substr(LPAD(:p_param7, 7, '0'),1,1)),
	decode(substr(LPAD(:p_param7, 7, '0'),2,1),'*','_',substr(LPAD(:p_param7, 7, '0'),2,1)),
	decode(substr(LPAD(:p_param7, 7, '0'),3,1),'*','_',substr(LPAD(:p_param7, 7, '0'),3,1)),
	decode(substr(LPAD(:p_param7, 7, '0'),4,1),'*','_',substr(LPAD(:p_param7, 7, '0'),4,1)),
	decode(substr(LPAD(:p_param7, 7, '0'),5,1),'*','_',substr(LPAD(:p_param7, 7, '0'),5,1)),
	decode(substr(LPAD(:p_param7, 7, '0'),6,1),'*','_',substr(LPAD(:p_param7, 7, '0'),6,1)),
	decode(substr(LPAD(:p_param7, 7, '0'),7,1),'*','_',substr(LPAD(:p_param7, 7, '0'),7,1)) into 
	car1,car2,car3,car4,car5,car6,car7
	from dual;
  :p_where :=  :p_where || ' and TO_CHAR(L.codsg, ''FM0000000'') like ' || '''' || car1 || car2 || car3 || car4 || car5 || car6 || car7 || '''';
END IF;
  SELECT moismens INTO :p_param10 FROM datdebex;
  
  ----srw.message(1,:p_where);
  return (TRUE);
end;
