end;

      function CF_NOM_PRE_CPFormula return VARCHAR2 is
     l_res VARCHAR2(100) := '';
begin
     l_res := RTRIM(:dc_nomcp);
     IF ( :dc_pnomcp IS NOT NULL )
  	THEN
		l_res := l_res || ' - ' || SUBSTR(:dc_pnomcp, 1, 1) || '.';  
	END IF;
	RETURN(l_res); 
end;

