function.textSource:
      function CF_NB_REALISEFormula return Number is
   NB_JOUR NUMBER;
begin
   NB_JOUR := 0;
   
   
 SELECT SUM(cusag) INTO NB_JOUR
       FROM PROPLUS
       WHERE tires=:ident
       AND cdeb=:lmoisprest
       AND (aist NOT IN ('FORMAT', 'ABSDIV') OR aist IS NULL);
	     
  return NB_JOUR; 
end;

      function AfterPForm return boolean is
begin
  SELECT TO_NUMBER(TO_CHAR(datdebex.datdebex,'YYYY')) INTO :P_ANNEE
  FROM datdebex;
  
  	
 	:P_PERIME:=pack_global.lire_perime(:P_GLOBAL);
  
	:P_WHERE := '  AND FDEPPOLE IN (SELECT codsg FROM vue_dpg_perime where INSTR('''||:P_PERIME||''', codbddpg) > 0 )';
													  
	
  return (TRUE);
end;

      function CF_1Formula return Number is
begin
	
--	return :CONSOM;
	return pack_utile_cout.AppliqueTauxHTR(:P_ANNEE,:CONSOM,:P_PARAM6,:filcode);
end;

      function CF_COUTFormula return Number is
CONSOM NUMBER;
begin
	

	 CONSOM := 0;        
  
  	SELECT SUM(pr.cusag*pack_utile_cout.getCoutHTR(:soccode,:rtype,:P_ANNEE,'',:niveau,:ldeppole1,:cout,TO_CHAR(:lmoisprest,'DD/MM/YYYY'),:filcode)) INTO CONSOM
     	 FROM PROPLUS pr
        WHERE pr.tires=:ident
        AND pr.cdeb=:lmoisprest
       	AND (pr.aist NOT IN ('FORMAT', 'ABSDIV') OR pr.aist IS NULL);
	      
	    return CONSOM + :CF_MONTANT_MENS;  
  
    EXCEPTION
           WHEN OTHERS THEN raise; --RETURN 0;
end;

      function CF_1FORMULA0005 return Number is
begin
  	return pack_utile_cout.AppliqueTauxHTR(:P_ANNEE,:CONSOM1,:P_PARAM6,:filcode1);
end;

      function CF_MONTANT_MENSFormula return Number is
montant_mens NUMBER;
begin
		 montant_mens := 0;
  		              
        SELECT nvl(montant_mensuel,0) INTO montant_mens
					FROM situ_ress
					WHERE ident=:ident
					AND datsitu=pack_situation_full.datsitu_ressource(:ident, :lmoisprest);
	 
   return pack_utile_cout.AppliqueTauxHTR(:P_ANNEE,montant_mens,:P_PARAM6,:filcode);
   
    EXCEPTION
           WHEN OTHERS THEN raise; --RETURN 0;

end;
