dataSource.select:
select sigdep, sigpole, codsg, libdsg, sum(PROP_FOURN) PROP_FOURN, sum(PROP_CLI) PROP_CLI, sum(NOTIFIE) NOTIFIE, sum(ARBITRE) ARBITRE, sum(REESTIME) REESTIME, sum(REALISE) REALISE, decode(sum(REESTIME),0,0,ROUND(((sum(REALISE))/(sum(REESTIME)))*100,1)) REAL_REESTIME, sum(RAF_BIP) RAF_BIP, sum(RAF_RESSOURCE) RAF_RESSOURCE, (sum(RAF_BIP)-sum(RAF_RESSOURCE)) ECARTS, decode(sum(REESTIME),0,0,ROUND((((sum(RAF_BIP)-sum(RAF_RESSOURCE)))/(sum(REESTIME)))*100,1)) ECART_ESTIME, sum(LIGNE) LIGNE, sum(RESSOURCE) RESSOURCE from (select D.MOISMENS MOISMENS, CA.THEORIQUE THEORIQUE, RA.CODSG CODSG, RA.CODSG ||' '|| SUBSTR(SI.LIBDSG,INSTR(SI.LIBDSG,SI.SIGPOLE)) libdsg, SI.sigpole sigpole, SI.sigdep sigdep, NVL(SUM(B.BPMONTME),0) PROP_FOURN, NVL(SUM(B.BPMONTMO),0) PROP_CLI, NVL(SUM(B.BNMONT),0) NOTIFIE, NVL(SUM(B.ANMONT),0) ARBITRE, NVL(SUM(B.REESTIME),0) REESTIME, NVL(SUM(C.CUSAG),0) REALISE, DECODE(NVL(SUM(B.REESTIME),0),0,0,NVL(ROUND((NVL(SUM(C.CUSAG),0)/SUM(B.REESTIME))*100,1),0)) REAL_REESTIME, NVL(SUM(B.REESTIME),0)-NVL(SUM(C.CUSAG),0) RAF_BIP, NVL(pack_ree_synthese_locale.RAF_RESSOURCE(RA.CODSG,RA.CODE_ACTIVITE,RS.CODE_SCENARIO,D.MOISMENS),0) RAF_RESSOURCE, (NVL(SUM(B.REESTIME),0)-NVL(SUM(C.CUSAG),0))-NVL(pack_ree_synthese_locale.RAF_RESSOURCE(RA.CODSG,RA.CODE_ACTIVITE,RS.CODE_SCENARIO,D.MOISMENS),0) ECARTS, NVL(SUM(B.ANMONT),0)-NVL(SUM(B.REESTIME),0) LIGNE, (NVL(SUM(B.ANMONT),0)-NVL(SUM(B.REESTIME),0))+(NVL(SUM(B.REESTIME),0)-NVL(SUM(C.CUSAG),0))-NVL(pack_ree_synthese_locale.RAF_RESSOURCE(RA.CODSG,RA.CODE_ACTIVITE,RS.CODE_SCENARIO,D.MOISMENS),0) RESSOURCE from REE_ACTIVITES RA, REE_ACTIVITES_LIGNE_BIP RAL, BUDGET B, CONSOMME C, DATDEBEX D, REE_SCENARIOS RS, STRUCT_INFO SI, CALENDRIER CA where RA.CODSG>=to_number(replace(:P_param6,'*','0')) and RA.CODSG<=to_number(replace(:P_param6,'*','9')) and RA.CODSG=RAL.CODSG(+) and RA.CODE_ACTIVITE=RAL.CODE_ACTIVITE(+) and RA.TYPE='N' and SI.CODSG=RA.CODSG and RAL.PID=B.PID(+) and B.ANNEE(+)=:P_ANNEE and RAL.PID=C.PID(+) and C.ANNEE(+)=:P_ANNEE and RA.CODSG=RS.CODSG and UPPER(RS.CODE_SCENARIO) like UPPER(pack_ree_synthese_locale.code_scenario('OFFIC')) and UPPER(RS.OFFICIEL) like UPPER(pack_ree_synthese_locale.officiel('OFFIC')) and CA.CALANMOIS=D.MOISMENS
&P_WHERE
GROUP BY RA.CODSG,RA.CODE_ACTIVITE,SI.CODSG,SI.LIBDSG,MOISMENS,RS.CODE_SCENARIO,RS.LIB_SCENARIO,RS.COMMENTAIRE,THEORIQUE,SI.sigpole,SI.sigdep) GROUP BY sigdep,sigpole,codsg,libdsg;

function.textSource:
      function BeforePForm return boolean is
begin
  select to_char(moismens, 'YYYY') into :P_ANNEE from datdebex;
  return (TRUE);
end;

      function CF_REAL_REESTIME_DEPFormula return Number is
begin
	if(:CS_REESTIME_DEP is not null and :CS_REESTIME_DEP != 0)then
  	return round((:CS_REALISE_DEP/:CS_REESTIME_DEP)*100,1);
  end if;
  return 0;
end;

      function CF_ECART_ESTIME_DEPFormula return Number is
begin
	if(:CS_REESTIME_DEP is not null and :CS_REESTIME_DEP != 0)then
  	return round((:CS_ECARTS_DEP/:CS_REESTIME_DEP)*100,1);
  end if;
  return 0;
end;

      function CF_REAL_REESTIMEFormula return Number is
begin
	if(:CS_REESTIME is not null and :CS_REESTIME != 0)then
  	return round((:CS_REALISE/:CS_REESTIME)*100,1);
  end if;
  return 0;
end;

      function CF_ECART_ESTIMEFormula return Number is
begin
	if(:CS_REESTIME is not null and :CS_REESTIME != 0)then
  	return round((:CS_ECARTS/:CS_REESTIME)*100,1);
  end if;
  return 0;
end;

      function R_TOTAL1FormatTrigger return boolean is
begin
  if(:cs_nb_pole>1)then
       return (TRUE);
  
  else 
  	   return (FALSE);
  end if;	   
end;

      function AfterPForm return boolean is

	l_perim_me	VARCHAR2(1000) := '';
	
begin

 	l_perim_me := pack_global.lire_perime(:P_GLOBAL);		
	:P_WHERE := ' AND RA.codsg IN (SELECT codsg FROM vue_dpg_perime where INSTR('''|| l_perim_me ||''', codbddpg) > 0 )'; 

  return (TRUE);
end;
