function.textSource:
      function CF_1FORMULA0013 return Number is
begin
  if(:TOT_REESTIME<>0)then
  	return (:TOT_REALISE/:TOT_REESTIME)*100;
  else 
  	return 0;
  END IF;	
end;

      function CF_1Formula return Number is
begin
   if(:TOT_REESTIME<>0)then
  	return (:TOT_ECARTS/:TOT_REESTIME)*100;
  else
  	return 0;
  end if;	
end;

      function AfterPForm return boolean is
begin
  -- 
  select to_char(moismens, 'YYYY') into :P_ANNEE from datdebex;
  return (TRUE);

  return (TRUE);
end;
