      function AfterPForm return boolean is

   l_annee0  VARCHAR2(10);
   l_annee1  VARCHAR2(10);
   p_dpg VARCHAR2(10) := '';
   l_perim_me	VARCHAR2(1000) := '';
   
begin
	
	l_perim_me := pack_global.lire_perime(:P_GLOBAL);		
	:P_WHERE :=' AND lb.codsg IN (SELECT codsg FROM vue_dpg_perime where INSTR('''|| l_perim_me ||''', codbddpg) > 0 )'; 

   -- ================================================
   -- Determiner l'annee de traitement N et N+1
   -- ================================================
   l_annee0 := to_char(add_months(sysdate,-1),'YYYY');
   l_annee1 := to_char(to_number(l_annee0,'FM9999') + 1,'FM9999');
   :p_where := :p_where || ' and budg.annee(+) = ' || l_annee0 ;
   :p_where := :p_where || ' and budg1.annee(+) = ' || l_annee1 ;
   :p_where := :p_where || ' and conso.annee(+) = ' || l_annee0 ;

   -- ================================================
   -- Soustraire les eventuels $ ou ' ' dans p_param6
   -- ================================================
   
   p_dpg := RTRIM(LTRIM(:P_PARAM6));
   p_dpg := REPLACE(p_dpg,'*','%');
   
   IF :P_PARAM6!= '*******' then 
   	
     	 :p_where := :p_where || ' and  to_char(lb.codsg,''FM0000000'') like ''' || p_dpg || '''';
   
   END IF;
   
   -- --srw.message(1,:P_where);  
  return (TRUE);
end;

      function CF_Annee0 return number is
  l_annee varchar2(4);
begin
  select to_char(add_months(sysdate,-1),'YYYY') into l_annee from dual;
  return to_number(l_annee,'FM9999');
end;

      function CF_AnN1 return Number is
  l_annee varchar2(4);
begin
  select to_char(add_months(sysdate,-1),'YYYY') into l_annee from dual;
  return to_number(l_annee,'FM9999') + 1;
end;

      function CF_TritypeFormula return VARCHAR2 is
begin
  return substr(:tritype,1,4) ;
end;

      function CF_TritypeLibFormula return VARCHAR2 is
begin
  return substr(:tritype,6,20) ;
end;

      function CF_MoisAnneeFormula return Date is
  l_annee date;
begin
  select add_months(sysdate,-1) into l_annee from dual;
  return l_annee;
end;
