      function F_CODEDPFormatTrigger return boolean is
Text varchar2(50);
begin
   If NVL(LENGTH(RTRIM(LPAD(:P_PARAM6, 7, '0'),'*')), 0)=6 then
	Text:='TOTAL DEP/POLE/GROUPE : '||LPAD(:P_PARAM6, 7, '0');
   else 
	Text:='TOTAL DEP/POLE : '||:CODEDP;
   end if;
   SRW.SET_FIELD_CHAR(0,Text);
  return (TRUE);
end;

      function M_2_FTRFormatTrigger return boolean is
begin
  If NVL(LENGTH(RTRIM(LPAD(:P_PARAM6, 7, '0'),'*')), 0)=3 then
 	return (TRUE);
  else
	return (FALSE);
  end if;
end;
