function.textSource:
      -- -------------------------------------------------------------------------
-- Supprime des cartactres '*', blancs ou '$' se trouvant à la fin de p_text 
-- Retourne la chaine resultante.
----------------------------------------------------------------------------
FUNCTION RtrimEtoile(P_Text In VARCHAR2) RETURN varchar2 IS
  meta VARCHAR2(1);
  L_Text varchar2(7);
BEGIN
  L_Text := P_Text;
  meta := substr(P_Text, NVL(length(P_Text), 0),1);
  if (meta = ' ') or (meta = '*') or (meta = '$') then
      L_Text := rtrim(P_Text,meta);   
  end if;
    
  return(L_Text);
END;

      -- ----------------------------------------------------------------------
-- Retourn la (date systeme - 1 Mois)
-- ----------------------------------------------------------------------
function CF_LastMonthDate return Date is
d date;
begin
	  select moismens into d from datdebex;

  return(d);
end;

      -- ----------------------------------------------------------------------
-- Retourn CF_AN0 + 1 (cf cf_an0)
-- ----------------------------------------------------------------------
function CF_AN1 return VARCHAR2 is
  YearOflastMonth number(4);
begin
 
  
   select to_char(moismens,'YYYY') into YearOflastMonth from datdebex;
  return (to_char(YearOflastMonth+1));
   
  
end;

      -- ----------------------------------------------------------------------
-- Retourn l'année du (date systeme - 1 Mois)
-- ----------------------------------------------------------------------
function CF_AN0 return VARCHAR2 is
  YearOflastMonth number(4);
begin
  select to_char(moismens,'YYYY') into YearOflastMonth from datdebex;
  return (to_char(YearOflastMonth));
   
end;

      -- ---------------------------------------------------------------------------------------
-- Avant de lancer l'édition on cree la table temporaire tmpRefTans
-- ---------------------------------------------------------------------------------------
function BeforeReport return boolean is
begin

	if (:p_menu = 'REF') then

		:p_numseq := pack_RefPCM.Select_DataReftrans(:p_param8,:p_param9,:p_param7, :P_GLOBAL);  
	else
		-- menu dir me mo
		 :p_numseq := pack_RefPCM.Select_DataReftrans(:p_param6,:p_param7,:p_param8, :P_GLOBAL);  
	end if;





  if (:p_numseq != 0) then
    return (true);
  else 
    return (false);
  end if;

end;

      function AfterReport return boolean is
   l_ret boolean ;
begin
  
   l_ret := pack_Refpcm.Delete_Donnees(:p_numseq);  
  return (l_ret);

end;

      function CF_Titre3Formula return VARCHAR2 is
begin
   

   
   if (:p_menu = 'REF') then
   		return  pack_RefTrans.Titre3(:P_PARAM8,:P_PARAM9,:P_PARAM7);
   else
   	-- menu dir me mo
   	return  pack_RefTrans.Titre3(:P_PARAM6,:P_PARAM7,:P_PARAM8);
   end if;
end;

      function F_METIERFormatTrigger return boolean is
begin

	if (:ORDER_METIER = 1) then
  		SRW.SET_FIELD_CHAR (0, 'Lignes Maîtrise d''oeuvre');
	else if (:ORDER_METIER = 2) then
			SRW.SET_FIELD_CHAR (0, 'Lignes Maîtrise d''ouvrage');
			else if (:ORDER_METIER = 3) then
					SRW.SET_FIELD_CHAR (0, 'Lignes Homologation');
					else
		 			SRW.SET_FIELD_CHAR (0, 'Autres lignes');
				  end if;
			end if;
	end if;

	return (TRUE);
end;

      function AfterPForm return boolean is
BEGIN
	 -- On vire tous les espaces
	 :P_PARAM6 := RTRIM(LTRIM(:P_PARAM6));
	 :P_PARAM7 := RTRIM(LTRIM(:P_PARAM7));
	 :P_PARAM8 := RTRIM(LTRIM(:P_PARAM8));
	 :P_PARAM9 := RTRIM(LTRIM(:P_PARAM9));
	 	
   -- Informations pour l'habilitation par référentiels
   :P_habili_DP := pack_global.lire_doss_proj(:P_GLOBAL);
  
   :P_habili_P  := pack_global.lire_projet(:P_GLOBAL);
   :P_habili_A  := pack_global.lire_appli(:P_GLOBAL);
	 :p_menu      := pack_global.lire_globaldata(:P_GLOBAL).menutil;
	 -- Initialisation de P_WHERE
	 :P_WHERE := '';
  
  
	--srw.message(1,:p_menu);

	
	
	/* 
	MENU REF : 
	p_param7 = dossier projet 
	p_param8 = projet
	p_param9 = application
	
	MENU DIR : 
	p_param6 = projet
	p_param7 = application
	p_param8 = dossier projet
	
	*/
	  
  IF (:p_menu = 'REF') THEN
  		:P_TITRE_ETAT:='Référentiels au choix';	
 
				-- On rempli la clause qui limite les résultats
			  -- Fonction de l'habilitation par référentiel
			  -- Dossier Projet demandé
			  IF (:P_PARAM6='DP') THEN
			  	:P_WHERE := 'AND ((INSTR(UPPER(:P_habili_DP), ''TOUS'') > 0)
			  								 OR (INSTR(:P_habili_DP, r.codgroup) > 0)
			  							)';
			  	--srw.message(1,:P_habili_DP);
			  	:p_param8 := null;
			  	:p_param9 := null;
			  -- Projet demandé
			  ELSIF (:P_PARAM6='P') THEN
			  	-- On fait aussi le contrôle que sur le projet principal (icpir) comme dans la proc
					:P_WHERE := 'AND ((INSTR(UPPER(:P_habili_P), ''TOUS'') > 0)
												 OR (INSTR(UPPER(:P_habili_DP), ''TOUS'') > 0)
												 OR	(INSTR(:P_habili_P, r.codgroup) > 0)
												 OR (INSTR(:P_habili_P, p.icpir) > 0)
												 OR (INSTR(:P_habili_DP, TO_CHAR(lb.dpcode, ''FM00000'')) > 0 )
											)';
					:p_param7 := null;
			  	:p_param9 := null;
				-- Application demandée
			  ELSIF (:P_PARAM6='A') THEN
			  	-- On fait aussi le contrôle sur l'application principale comme dans la proc
					:P_WHERE := 'AND ((INSTR(UPPER(:P_habili_A), ''TOUS'') > 0)
												 OR (INSTR(UPPER(:P_habili_P), ''TOUS'') > 0)
												 OR (INSTR(UPPER(:P_habili_DP), ''TOUS'') > 0)
												 OR (INSTR(:P_habili_A, r.codgroup) > 0)
												 OR (INSTR(:P_habili_A, a.acdareg) > 0)
												 OR (INSTR(:P_habili_P, lb.icpi) > 0) 
												 OR (INSTR(:P_habili_DP, TO_CHAR(lb.dpcode, ''FM00000'')) > 0 )
				     					 )';
				  :p_param7 := null;
			  	:p_param8 := null;
				END IF; 
			
			
				-- Si un élément spécifique est sélectionné pour l'habili par ref
				-- On le met dans P_PARAM6
			  IF :P_PARAM6='DP' AND :P_PARAM7 = 'TOUS' THEN
			  	 :P_PARAM7 := :P_PARAM6;
			  ELSIF :P_PARAM6='P' AND :P_PARAM8 = 'TOUS' THEN
			  	:P_PARAM8 := :P_PARAM6;
			  ELSIF :P_PARAM6='A' AND :P_PARAM9 = 'TOUS' THEN
			  	 :P_PARAM9 := :P_PARAM6;
	
	
			  END IF;
 
			  ELSE
			  	
 			:P_TITRE_ETAT:='Suivi par métier/référentiel';
 
  	END IF;
 
  	
 
  RETURN TRUE;
end;

      function B_DonneesRestreintesFormatTrig return boolean is
nbLignesMax NUMBER(10);
begin
	nbLignesMax := pack_reftrans.Get_Nb_Lignes(:codgroup,:p_numseq);
  IF (:CS_NbLignes =nbLignesMax) THEN
  	return (FALSE);
  ELSE
  	return (TRUE);
  END IF;
end;

