      function F_CODCAMOCA1FormatTrigger return boolean is
affiche boolean;

begin
      -- Généré automatiquement à partir de Report Builder.
  
  affiche := FALSE;
  
  if (:p_menu != 'REF' and (:P_PARAM7 is not null or :P_PARAM6 is not null) ) then
  		affiche := TRUE;
	  if (:P_PARAM7 is not null) then
	  	SRW.SET_FIELD_CHAR(0, :CLIBCA5);
	  else
	  	SRW.SET_FIELD_CHAR(0, :CLIBCA15);
	  end if;
  
  end if;
  
  if (:p_menu = 'REF' and :P_PARAM9 is not null or :P_PARAM8 is not null) then
    		affiche := TRUE;
 		if (:P_PARAM9 is not null) then
  	SRW.SET_FIELD_CHAR(0, :CODCAMO1);
  else
  	SRW.SET_FIELD_CHAR(0, :CODCAMO11);
  end if;
end if; 
  return (affiche);

end;

