      ------------------------------------------------------------------------------------------
-- Rôle : Construire un Where conditionnel.
------------------------------------------------------------------------------------------
function AfterPForm return boolean is

	l_global pack_global.GlobalData;

BEGIN

	-- recuperation de la date d'arreté
	SELECT moismens,TO_NUMBER(TO_CHAR(datdebex,'yyyy'))
	into :P_DAT_ARR,:P_ANNEE
	FROM datdebex;
	
 	l_global := pack_global.lire_globaldata(:P_GLOBAL);

  :P_APP := l_global.appli;
  :P_APP := REPLACE(:P_APP,',',''',''');
  
	:P_DOS_PROJ := l_global.doss_proj;
  
  :P_PROJET := l_global.projet;
  :P_PROJET := REPLACE(:P_PROJET,',',''',''');
  
  :P_CA_PAYEUR := l_global.ca_payeur;
  
  :P_WHERE := ' ';	
  
  RETURN TRUE;
end;

      function CF_LastMonthDateFormula return Date is
d date;
begin
  select ADD_MONTHS(sysdate,-1) into d from dual;
  return(d);
end;

      -- ----------------------------------------------------------------------
-- Retourn CF_AN0 + 1 (cf cf_an0)
-- ----------------------------------------------------------------------
function CF_AN1 return VARCHAR2 is
  YearOflastMonth number(4);
begin
  select to_char(ADD_MONTHS(sysdate,-1), 'YYYY') into YearOflastMonth from dual;
  return (to_char(YearOflastMonth + 1));
end;

      -- ----------------------------------------------------------------------
-- Retourn l'année du (date systeme - 1 Mois)
-- ----------------------------------------------------------------------
function CF_AN0 return VARCHAR2 is
  YearOflastMonth number(4);
begin
select to_char(ADD_MONTHS(sysdate,-1), 'YYYY') into YearOflastMonth from dual;
  return (to_char(YearOflastMonth));
end;

      function CF_1Formula return char is
begin
  IF (:TYPE_DP = '3') THEN
  	return ('JH - PROJETS T1 > 10M¿');
  	ELSIF (:TYPE_DP = '2') THEN
  	return ('JH - PROJETS T1 DE 2 A 10M¿');
    		ELSIF (:TYPE_DP = '1') THEN
  		  return ('JH - PROJETS T1 DE 1 A 10M¿');
  		 			ELSIF (:TYPE_DP = '0') THEN
  		    	return ('ENVELOPPE DOMAINE');
  						ELSE 
  							return(:TYPE_DP);
  			END IF;
end;

