      function BeforeReport return boolean is

l_ca_payeur VARCHAR2(200);

begin
   
	 	-- CENTRE ACTIVITE
	 	-- Sélection du parametre à envoyer à la procédure stockée
	  if(:P_PARAM6 IS NOT NULL and :P_PARAM6 <> ' TOUS') THEN
	  	l_ca_payeur :=  :P_PARAM6 ;
	  else
	 	  	if(:P_CA_PAYEUR IS NOT NULL and :P_CA_PAYEUR <> 'TOUS' ) THEN
			   	l_ca_payeur := :P_CA_PAYEUR ;
				end if ;
	  end if;
   
   
  -- Rechercher les donnees et les inserer dans la table TMP_SYNTHCOUTPROJ
  :p_numseq := pack_hab_ref.Select_Donnees(:P_PARAM7, l_ca_payeur, :P_DOS_PROJ, :P_PROJET, :P_APP);
  if (:p_numseq != 0) then
    return (true);
  else 
    return (false);
  end if;
end;

      function AfterReport return boolean is
l_ret boolean ;
begin
  l_ret := pack_hab_ref.Delete_Donnees(:p_numseq);  
  return (l_ret);
end;

      function CF_TOT_NOTIFIEFormula return Number is
l_res number;
begin
  l_res := :NOTIFIE_LOG + :NOTIFIE_MOB + :NOTIFIE_MAT + :BUDGET1 ;
  return (l_res);
end;

      function CF_TOT_REALISEFormula return Number is
l_res number;
