function.textSource:
      function AfterPForm return boolean is
BEGIN
	
	
	if (:P_PARAM6 is not null) then
		:P_WHERE := :P_WHERE || ' AND dvf.IDDEM = ' || :P_PARAM6;
	end if;
  
	if ( NVL(LENGTH(:P_WHERE), 0) != 0 )
	then
		--srw.message(1, '[P_WHERE] : ' || :P_WHERE);
	end if;

  RETURN TRUE;
end;

      function M_VALIDEFormatTrigger return boolean is
begin
  
  if (:statut = 'V') then
  	  return (TRUE);
  end if;
  return (FALSE);
end;

      function M_SUSPENSFormatTrigger return boolean is
begin
  if (:statut = 'S') then
  	return (TRUE);
  end if;
  return (FALSE);
end;

      function CF_RESSOURCEFormula return Char is
begin
  return :ident1||' '||:rnom||' '||:rprenom;
end;

      function CF_SOCIETEFormula return Char is
begin
  return :socfact||' - '||:soclib;
end;

      function CF_STATUTFormula return Char is
begin
  if (:statut = 'V') then
  	return 'Validée';
  elsif (:statut = 'S') then
  	return 'Mise en suspens';
  end if;
  return 'En attente';
end;
