function.textSource:
      function AfterPForm return boolean is
	
	
  p_dpg VARCHAR2(10) := '';
	l_perim_me	VARCHAR2(1000) := '';
	

begin
	
	if (:P_PARAM6 is not null) THEN
		
		l_perim_me := pack_global.lire_perime(:P_GLOBAL);		
		:P_WHERE :=' AND bip.codsg IN (SELECT codsg FROM vue_dpg_perime where INSTR('''|| l_perim_me ||''', codbddpg) > 0 ) '; 
		
		p_dpg := RTRIM(LTRIM(:P_PARAM6));

		:P_WHERE :=	:P_WHERE || ' AND info.TOPFER = ''O'' ';
	  :P_WHERE :=	:P_WHERE || ' AND bip.CODSG >= to_number('''|| replace(p_dpg,'*','0') || ''') ';
	  :P_WHERE :=	:P_WHERE || ' AND bip.CODSG <= to_number('''|| replace(p_dpg,'*','9') || ''') '; 	

	else
			
		:P_WHERE := ' AND  bip.pid IN (''' || :P_PARAM12 ||''',''' || :P_PARAM7 ||'''
			,''' || :P_PARAM8 ||''',''' || :P_PARAM9 ||''',''' || :P_PARAM10 ||''',''' || :P_PARAM11 ||''') '; 
		
	end if;
 
	RETURN(TRUE);
  
end;

      function BeforePForm return boolean is
begin
  /* Calcule de l'année courante : Année N */
	SELECT TO_NUMBER( TO_CHAR( datdebex, 'YYYY' ) ) 
	INTO :P_ANNEE_EN_COURS
	FROM datdebex;
	
	--srw.message(1, 'Année N = ' || TO_CHAR(:P_ANNEE_EN_COURS));
  

  return (TRUE);
end;

      function BeforeReport return boolean is

	l_date_traite DATE; /* Date à prendre en compte pour le traitement */
begin

  SELECT datdebex INTO l_date_traite FROM datdebex;
  :p_numseq_2 := pack_visuprojprin.tmp_visuprojprin_budget2(rtrim(:p_param6, '*'),
						  :p_param7,
						  :p_param8,
						  :p_param9,
						  :p_param10,
						  :p_param11,
						  :p_param12,
						  l_date_traite);

  IF :p_numseq_2 = 0 THEN
	return (FALSE);
  END IF;

  return (TRUE);

end;

      function AfterReport return boolean is
begin


 -- supprime les lignes de l'edition dans la table
  delete from tmp_visuprojprin
  where  numseq = :p_numseq_2;
commit;

  return (true);   -- OK

   exception
      when others then
         return(false); -- NOK
  
  return (TRUE);
end;

      function F_dc_immos_ke1FormatTrigger return boolean is
begin
