function.textSource:
      function AfterPForm return boolean is
      l_where_dpg VARCHAR2(1000) := '';
      p_dpg VARCHAR2(10) := '';
      l_p_code_dpg VARCHAR2(10) := '';
      l_nom_colonne VARCHAR2(10) := '';
      l_perim VARCHAR2(1000) := '';
BEGIN
	
	SELECT TO_NUMBER(TO_CHAR(datdebex.datdebex,'YYYY'))
	  INTO :P_ANNEE
    FROM datdebex;
  
	-- On crée la where clause avec filtre sur le DPG si besoin
	-- paramètre :
	--        P_PARAM6 : variable où se trouve le DPG saisi à l'écran
	--        l_nom_colonne : nom de la colonne de la requête où l'on trouve le DPG
	
	-- On enlève tous les espaces
	p_dpg := RTRIM(LTRIM(:P_PARAM6));
	l_nom_colonne := 'l.CODSG';
	
  :p_perimetre := '';
	------------------------------------------------------------------------
 	-- Conditions WHERE sur dpg
 	-----------------------------------------------------------------------
	IF LPAD(p_dpg, 7, '*')='*******' OR p_dpg IS NULL THEN
	  l_where_dpg := '';
	ELSE
	  BEGIN
 		  -- DIRECTION
	  	select distinct(d.libdir) 
	  	  into :p_perimetre 
	  	  from directions d, struct_info s
	  	 where d.coddir = s.coddir 
	  	   and s.topfer = 'O'
	  	   and to_number(substr(LPAD(s.codsg,7,'0'), 1, 3)) = to_number(substr(LPAD(p_dpg, 7, '0'), 1, 3))
	  	   and d.libdir is not null
	  	   and rownum = 1;
	  EXCEPTION
	  	WHEN OTHERS THEN
	  		:p_perimetre := '';
	  END;
		--srw.message(1, '[DIRECTION] : ' || :p_perimetre);
		  
	  IF SUBSTR(LPAD(p_dpg, 7, '0'), 4, 4)='****' THEN
		  l_p_code_dpg := SUBSTR(LPAD(p_dpg, 7, '0'), 1, 3)||'____';
	  ELSE
		  IF SUBSTR(LPAD(p_dpg, 7, '0'), 6, 2)='**' THEN
		    l_p_code_dpg := SUBSTR(LPAD(p_dpg, 7, '0'),1,5)||'__';
		  
			  BEGIN
	  		  -- DEPARTEMENT
			  	select distinct sigdep||'/'||sigpole into l_perim from struct_info 
			  	 where to_number(substr(LPAD(codsg,7,'0'), 1, 5)) = to_number(SUBSTR(LPAD(p_dpg, 7, '0'), 1, 5))
			  	   and topfer  = 'O';
			  	if :p_perimetre = '' then
			  		:p_perimetre := l_perim;
			  	else
			  		:p_perimetre := :p_perimetre||'/'||l_perim;
			  	end if;
			  EXCEPTION
			  	WHEN NO_DATA_FOUND THEN
			  		:p_perimetre := SUBSTR(LPAD(p_dpg, 7, '0'), 1, 3);
			  	WHEN TOO_MANY_ROWS THEN
			  		-- 1 pole avec plusieurs libellé distinct
			  		BEGIN
					  	select distinct(sigdep) into l_perim from struct_info 
					  	 where to_number(substr(LPAD(codsg,7,'0'), 1, 3)) = to_number(SUBSTR(LPAD(p_dpg, 7, '0'), 1, 3))
					  	   and topfer  = 'O'
					  	   and rownum = 1;
					  	if length(:p_perimetre) = 0 then
					  		:p_perimetre := l_perim;
					  	else
					  		:p_perimetre := :p_perimetre||'/'||l_perim;
					  	end if;
					  EXCEPTION
					  	WHEN OTHERS THEN
					  		:p_perimetre := SUBSTR(LPAD(p_dpg, 7, '0'), 1, 3);
					  END;
			  	WHEN OTHERS THEN
			  		:p_perimetre := '';
			  END;
		  
		  ELSE
		    l_p_code_dpg := LPAD(p_dpg, 7, '0');
			  BEGIN
	  		  -- DEPARTEMENT
			  	--select sigdep||'/'||sigpole into l_perim from struct_info 
			  	select libdsg into :p_perimetre from struct_info 
			  	 where codsg = to_number(LPAD(p_dpg, 7, '0'))
			  	   and topfer  = 'O';
			  	--if length(:p_perimetre) = 0 then
			  	--	:p_perimetre := l_perim;
			  	--else
			  	--	:p_perimetre := :p_perimetre||'/'||l_perim;
			  	--end if;
			  EXCEPTION
			  	WHEN NO_DATA_FOUND THEN
			  		:p_perimetre := SUBSTR(LPAD(p_dpg, 7, '0'), 1, 3);
			  	WHEN OTHERS THEN
			  		:p_perimetre := '';
			  END;
		  END IF;
		END IF;

		--srw.message(1, 'P_PARAM6 : ' || l_p_code_dpg);
    
	  l_where_dpg := ' TO_CHAR('||l_nom_colonne||', ''FM0000000'') like ''' || l_p_code_dpg || ''' ';
	END IF;

	if ( NVL(LENGTH(l_where_dpg), 0) != 0 ) then 
		:P_WHERE := :P_WHERE || ' AND ( ' || l_where_dpg || ' ) ';
	end if;
	
  RETURN TRUE;
end;

      function ProposéFournAA1Formula return Number is
   p_propo1 number;
begin
  select nvl(b.BPMONTME,0) 
    into p_propo1
    from budget b 
   where :Ligne_Bip = b.pid
     and b.ANNEE = :P_ANNEE+1;
     
  return p_propo1;
exception
	when no_data_found then
	  return 0;
end;

      function ANNEEFormula return Number is
begin
  return :p_annee;
end;

      function DATE_EXTRACTFormula return Date is
begin
  return sysdate;
end;

      function PERIMETREFormula return Char is
begin
    return :p_perimetre;
end;
