      function AfterPForm return boolean is

	chaine_ca_payeur VARCHAR2(5000);
	l_ca_payeur VARCHAR2(5000);
	l_compteur NUMBER;
	l_perimcli VARCHAR2(255);
	p_where_perim VARCHAR2(5000); 
	l_menu VARCHAR2(25);

begin

 
	   /*CMA Fiche 1176 09/05/2011 le perimo est différent si l'utilisateur est dans le menu Client*/
		l_menu := pack_global.lire_menutil(:P_global);
		
		if('MO'=l_menu)then
			l_perimcli := pack_global.lire_perimcli(:P_global);
		else
			l_perimcli := pack_global.lire_perimo(:P_global);
		end if;
	  
	  :P_CA_PAYEUR := pack_global.lire_ca_payeur(:p_global);
	  p_where_perim := ' ';
	  		
		if length(l_perimcli) != 0 then	  	
				p_where_perim := ' and ( lb.clicode in (select clicode from vue_clicode_perimo where BDCLICODE in ('||l_perimcli||') ) 
																or lb.clicode_oper in (select clicode from vue_clicode_perimo where BDCLICODE in ('||l_perimcli||') ) )';
		end if;
		
		if(:p_param7 = 'Pas de limitation') then
			chaine_ca_payeur := '';
		elsif(:p_param7 = 'Tous')	then
			if(:P_CA_PAYEUR IS NOT NULL AND :P_CA_PAYEUR <> 'Tous' ) then
	  		chaine_ca_payeur := :P_CA_PAYEUR;
			else
				chaine_ca_payeur := '';	
			end if;	
		else
			chaine_ca_payeur := :p_param7;
		end if;	
			
	  :p_from :='(';
	  l_compteur := 1 ;
  
     while (length(chaine_ca_payeur) != 0) loop
     
     if (instr(chaine_ca_payeur,',') != 0) then
          l_ca_payeur := substr(chaine_ca_payeur,0,instr(chaine_ca_payeur,',')-1);
          chaine_ca_payeur := substr(chaine_ca_payeur,instr(chaine_ca_payeur,',')+1);
     else
        l_ca_payeur := chaine_ca_payeur;
        chaine_ca_payeur := '';
     end if;
     
    
     
        if (l_compteur != 1) then
            :p_from := :p_from || 'UNION';
    
        end if;
             
     if (pack_global.recherche_niveau(l_ca_payeur) != 99) then
     
     if (pack_global.recherche_niveau(l_ca_payeur) = 0) then
   
             	:p_from := :p_from||'(
                            select codcamo 
							from centre_activite
							where 
							codcamo IN ('|| l_ca_payeur || ')
--PPM 58986 : Bascule FI (modification de l année civile du 12/N-1 au 11/N par 01/N au 12/N)
										AND (cdateferm is null or cdateferm between to_date(''01/''||to_number(to_char(sysdate,''YYYY'')),''MM/YYYY'') and
last_day(to_date(''12/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))				)';	 
							
      
     elsif (pack_global.recherche_niveau(l_ca_payeur) = 1) then
            	
                :p_from := :p_from||'(						 		        
						     select codcamo 
						     from centre_activite
							 where 
								 CANIV1 IN ('|| l_ca_payeur || ')
							AND (cdateferm is null or substr(TO_DATE(cdateferm, ''DD/MM/YYYY''), 7, 8) = substr(TO_DATE(sysdate, ''DD/MM/YYYY''), 7, 8))
								)';	      
           elsif (pack_global.recherche_niveau(l_ca_payeur) = 2) then
             :p_from := :p_from||' (						 		        
						 		        select codcamo 
													from centre_activite
													where 
														CANIV2 IN ('|| l_ca_payeur|| ')
--PPM 58986 : Bascule FI (modification de l année civile du 12/N-1 au 11/N par 01/N au 12/N)														
														AND (cdateferm is null or cdateferm between to_date(''01/''||to_number(to_char(sysdate,''YYYY'')),''MM/YYYY'') and
last_day(to_date(''12/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))
					)';
               elsif (pack_global.recherche_niveau(l_ca_payeur) = 3) then
                :p_from := :p_from||' (						 		        
						 		        select codcamo 
													from centre_activite
													where 
												 			CANIV3 IN ('|| l_ca_payeur || ')
--PPM 58986 : Bascule FI (modification de l année civile du 12/N-1 au 11/N par 01/N au 12/N)															
														AND (cdateferm is null or cdateferm between to_date(''01/''||to_number(to_char(sysdate,''YYYY'')),''MM/YYYY'') and
last_day(to_date(''12/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))
												)';	 
                elsif (pack_global.recherche_niveau(l_ca_payeur) = 4) then       
                :p_from :=:p_from|| '
					 		         (						 		        
						 		        select codcamo 
													from centre_activite
													where 
																CANIV4 IN ('|| l_ca_payeur || ')
--PPM 58986 : Bascule FI (modification de l année civile du 12/N-1 au 11/N par 01/N au 12/N)																
															AND (cdateferm is null or cdateferm between to_date(''01/''||to_number(to_char(sysdate,''YYYY'')),''MM/YYYY'') and
last_day(to_date(''12/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))
												)';
								elsif (pack_global.recherche_niveau(l_ca_payeur) = 5) then       
                :p_from :=:p_from|| '
					 		         (						 		        
						 		        select codcamo 
													from centre_activite
													where 
																CANIV5 IN ('|| l_ca_payeur || ')
--PPM 58986 : Bascule FI (modification de l année civile du 12/N-1 au 11/N par 01/N au 12/N)																
															AND (cdateferm is null or cdateferm between to_date(''01/''||to_number(to_char(sysdate,''YYYY'')),''MM/YYYY'') and
last_day(to_date(''12/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))
												)';
										elsif (pack_global.recherche_niveau(l_ca_payeur) = 6) then       
                :p_from :=:p_from|| '
					 		         (						 		        
						 		        select codcamo 
													from centre_activite
													where 
																CANIV6 IN ('|| l_ca_payeur || ')
--PPM 58986 : Bascule FI (modification de l année civile du 12/N-1 au 11/N par 01/N au 12/N)																
															AND (cdateferm is null or cdateferm between to_date(''01/''||to_number(to_char(sysdate,''YYYY'')),''MM/YYYY'') and
last_day(to_date(''12/''||to_char(sysdate,''YYYY''),''MM/YYYY'')))
												)';
                  	
          end if;
           
        l_compteur := l_compteur+1;
       end if;
      END LOOP;      
    
    :p_from := :p_from || ')';
    

    if  (:p_from = '()') then
    		:p_where := 'from stock_fi s,ligne_bip lb, synthese_fin_ress sfr
    		  where s.ident= sfr.ident(+)
					and s.pid = sfr.pid(+)
					and s.codcamo = sfr.codcamo(+)
					and s.cafi = sfr.cafi(+)
					and s.PID = lb.PID(+)'
					||p_where_perim||
					' group by s.pid, s.cdeb, s.ident, s.cafi, s.typproj , s.metier , s.pnom,s.codsg,s.dpcode,s.icpi,s.codsgress,s.libdsg,s.codcamo,s.clibrca,s.rtype,s.soccode,s.coutftht,s.coutft,s.coutenv
					order by s.pid, s.cdeb, s.ident, s.cafi
				';
    else
    		:p_where := 'from stock_fi s,ligne_bip lb, synthese_fin_ress sfr, '||:p_from||' liste_ca	
    		  where s.ident= sfr.ident(+)
					and s.pid = sfr.pid(+)
					and s.codcamo = sfr.codcamo(+)
					and s.cafi = sfr.cafi(+)
					and s.PID = lb.PID(+)
					and s.codcamo = liste_ca.codcamo'
					||p_where_perim||
					' group by s.pid, s.cdeb, s.ident, s.cafi, s.typproj , s.metier , s.pnom,s.codsg,s.dpcode,s.icpi,s.codsgress,s.libdsg,s.codcamo,s.clibrca,s.rtype,s.soccode,s.coutftht,s.coutft,s.coutenv
					order by s.pid, s.cdeb, s.ident, s.cafi
				';
    end if;	
    
    --srw.message(1,''||:p_where);	
     --srw.message(1,'toto :'||length(:p_from));	
    
  return (TRUE);
end;

      function G_pidGroupFilter return boolean is
begin
  if (length(:p_from)<30000)then
    return (TRUE);
  else           
  	 return (FALSe);
  end if;
end;
