function.textSource:
      function AfterPForm return boolean is
begin
  
  
  :P_MENU:='';
  :P_PERIME:='';
	--:P_PERIMO:='';
	:P_PERIMCLI:='';
	:P_WHERE := '';   
	

	
	:P_MENU:=pack_global.lire_globaldata(:P_GLOBAL).menutil;
	:P_PERIME:=pack_global.lire_perime(:P_GLOBAL);
	--:P_PERIMO:=pack_global.lire_perimo(:P_GLOBAL);
	:P_PERIMCLI:=pack_global.lire_perimcli(:P_GLOBAL);
  
  
  IF( UPPER(:P_MENU) = 'ME' )THEN
  	
  	
  	:P_WHERE :=	 :P_WHERE || ' AND ( ';
  	
    :P_WHERE :=	 :P_WHERE || ' l.codsg IN (SELECT codsg FROM vue_dpg_perime where INSTR('''||:P_PERIME||''', codbddpg) > 0 ) '; 

    :P_WHERE :=	:P_WHERE || ' OR  EXISTS ( SELECT 1 FROM STOCK_FI sf WHERE sf.pid=s.pid ';	
    :P_WHERE :=	:P_WHERE || '      AND sf.codcamo=s.codcamo ';	
    :P_WHERE :=	:P_WHERE || '      AND sf.cafi=s.cafi ';	
    :P_WHERE :=	:P_WHERE || '      AND DECODE(sf.rtype,''P'',DECODE(sf.soccode,''SG..'',''A'',''P''),sf.rtype)=s.rtype ';	
	  :P_WHERE :=	:P_WHERE || '      AND sf.codsgress IN (SELECT codsg FROM vue_dpg_perime WHERE INSTR('''||:P_PERIME||''', codbddpg) > 0)) ';	
  	:P_WHERE :=	:P_WHERE || ' ) ';  
	 	--IF( INSTR(:P_PERIMO, '88888')<=0  )THEN
    --   :P_WHERE :=	:P_WHERE || ' OR l.clicode IN (SELECT clicode FROM vue_clicode_perimo where INSTR('''||:P_PERIMO||''', bdclicode) > 0 )) ';
  	--ELSE
  	--	 :P_WHERE :=	:P_WHERE || ' ) ';
 	  --END IF;

	ELSIF( UPPER(:P_MENU) = 'MO' )THEN
	 	
	 		 IF( INSTR(:P_PERIMCLI, '88888')<=0  )THEN
      	 	:P_WHERE :=	' AND l.clicode IN (SELECT clicode FROM vue_clicode_perimo where INSTR('''||:P_PERIMCLI||''', bdclicode) > 0 ) ';
  		 ELSE
  		 		:P_WHERE :=	'';
 			 END IF;
  	
  	
  END IF;
  
  return (TRUE);
end;

      function P_PERIMCLIValidTrigger return boolean is
begin
  
  return (TRUE);
end;
